import React from 'react';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import {
    Terminal,
    FileCode,
    Codepen,
    Coffee,
    CheckCircle2,
    Map,
    Zap,
    Trophy,
    BookOpen,
    Target,
    Settings
} from 'lucide-react';
import YipHead from '../assets/Yip_head.png';

const Journeys = () => {
    const navigate = useNavigate();
    const [selectedCourse, setSelectedCourse] = React.useState(localStorage.getItem('selectedCourse') || 'html');

    const courses = [
        {
            id: 'python',
            name: 'Python',
            icon: <Terminal className="w-12 h-12 text-blue-400" />,
            color: 'from-blue-500/10 to-blue-600/5'
        },
        {
            id: 'html',
            name: 'HTML',
            icon: <FileCode className="w-12 h-12 text-orange-400" />,
            color: 'from-orange-500/10 to-orange-600/5'
        },
        {
            id: 'javascript',
            name: 'JavaScript',
            icon: <Codepen className="w-12 h-12 text-yellow-400" />,
            color: 'from-yellow-500/10 to-yellow-600/5'
        },
        {
            id: 'java',
            name: 'Java',
            icon: <Coffee className="w-12 h-12 text-red-400" />,
            color: 'from-red-500/10 to-red-600/5'
        }
    ];

    return (
        <div className="flex min-h-screen bg-[#111827] text-white font-['Inter',sans-serif]">

            {/* LEFT SIDEBAR: Navigation (Compact) */}
            <aside className="w-16 md:w-24 fixed left-0 top-0 bottom-0 bg-[#1f2937] border-r border-white/5 flex flex-col items-center py-6 z-50">
                <div className="mb-8 px-2 transition-transform hover:scale-110 cursor-pointer" onClick={() => navigate('/dashboard')}>
                    <img src={YipHead} alt="Yip" className="w-full h-auto drop-shadow-[0_4px_15px_rgba(74,144,226,0.5)]" />
                </div>

                <nav className="flex flex-col gap-6">
                    <NavItem icon={<Map className="w-6 h-6" />} onClick={() => navigate('/dashboard')} />
                    <NavItem icon={<Zap className="w-6 h-6" />} />
                    <NavItem icon={<Trophy className="w-6 h-6" />} />
                    <NavItem icon={<BookOpen className="w-6 h-6" />} />
                    <NavItem icon={<Target className="w-6 h-6" />} />
                </nav>

                <div className="mt-auto">
                    <NavItem icon={<Settings className="w-7 h-7" />} />
                </div>
            </aside>

            {/* MAIN CONTENT AREA */}
            <main className="flex-1 ml-16 md:ml-24 p-8 lg:p-12">
                <div className="max-w-6xl mx-auto space-y-12">

                    {/* Header Section */}
                    <div className="space-y-2">
                        <h1 className="text-4xl font-black italic uppercase tracking-tighter text-white">Coding Journeys</h1>
                        <div className="h-1.5 w-20 bg-blue-600 rounded-full" />
                    </div>

                    {/* Courses Grid */}
                    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
                        {courses.map((course) => (
                            <motion.button
                                key={course.id}
                                whileHover={{ y: -5 }}
                                whileTap={{ scale: 0.98 }}
                                onClick={() => setSelectedCourse(course.id)}
                                className={`relative group p-8 rounded-[2rem] border-2 transition-all text-left flex flex-col items-center justify-center gap-6 overflow-hidden
                                    ${selectedCourse === course.id
                                        ? 'bg-[#1f2937] border-blue-500 shadow-[0_0_40px_rgba(59,130,246,0.1)]'
                                        : 'bg-[#1f2937]/50 border-white/5 hover:border-white/10'
                                    }`}
                            >
                                {/* Gradient Background */}
                                <div className={`absolute inset-0 bg-gradient-to-br ${course.color} opacity-50 group-hover:opacity-100 transition-opacity`} />

                                {/* Selection Checkmark */}
                                {selectedCourse === course.id && (
                                    <div className="absolute top-4 right-4 text-blue-500">
                                        <CheckCircle2 className="w-6 h-6 fill-current bg-white rounded-full" />
                                    </div>
                                )}

                                {/* Icon */}
                                <div className="relative transform group-hover:scale-110 transition-transform duration-500">
                                    {course.icon}
                                    <div className="absolute inset-0 blur-2xl opacity-20 bg-current" />
                                </div>

                                {/* Text Info */}
                                <div className="relative text-center space-y-1">
                                    <h3 className="text-2xl font-black italic uppercase tracking-tight text-white group-hover:text-blue-400 transition-colors">
                                        {course.name}
                                    </h3>
                                </div>
                            </motion.button>
                        ))}
                    </div>

                    {/* Action Section (Optional, matches reference feel) */}
                    <motion.div
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        className="pt-12 flex justify-center"
                    >
                        <button
                            className="bg-blue-600 hover:bg-blue-500 text-white font-black italic uppercase px-12 py-4 rounded-2xl shadow-xl shadow-blue-900/20 transition-all active:scale-95"
                            onClick={() => {
                                localStorage.setItem('selectedCourse', selectedCourse);
                                localStorage.setItem('selectedSectionId', 1); // Default to section 1
                                navigate('/sections');
                            }}
                        >
                            Start Journey
                        </button>
                    </motion.div>
                </div>
            </main>
        </div>
    );
};

const NavItem = ({ icon, active = false, onClick }) => (
    <button
        onClick={onClick}
        className={`w-10 h-10 flex items-center justify-center rounded-xl transition-all ${active ? 'bg-blue-600 text-white shadow-lg shadow-blue-600/30' : 'text-slate-400 hover:bg-white/5 hover:text-white'
            }`}>
        {icon}
    </button>
);

export default Journeys;
