import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import Lottie from 'lottie-react';
import { Eye, EyeOff } from 'lucide-react';
import animationData from './assets/login-animation.json';
import logo from './assets/Logo.jpeg';
import './Login.css';

const Login = () => {
    const navigate = useNavigate();
    const [showPassword, setShowPassword] = useState(false);
    const lottieRef = useRef(null);

    return (
        <div className="main-wrapper">
            <div className="stars-layer"></div>
            <div className="glass-card">
                <img src={logo} alt="Trellis" className="brand-logo" />
                <div className="animation-container">
                    <Lottie lottieRef={lottieRef} animationData={animationData} loop={true} />
                </div>
                <form className="full-width-form">
                    <h2 className="auth-title">Login</h2>
                    <div className="input-container">
                        <input type="email" placeholder="Email Address" required />
                    </div>
                    <div className="input-container">
                        <input type={showPassword ? "text" : "password"} placeholder="Password" required />
                        <button type="button" className="eye-icon-button" onClick={() => setShowPassword(!showPassword)}>
                            {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
                        </button>
                    </div>
                    <button type="submit" className="action-button">LOGIN</button>
                    <div className="footer-nav">
                        <span>New to Trellis?</span>
                        <button type="button" className="nav-link-btn" onClick={() => navigate('/register')}>Sign Up</button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default Login;
