import React, { useState } from 'react';
import Lottie from 'lottie-react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

// Assets - Verify these names in your assets folder
import mascot from '../assets/trellis_mascot.png';
import logo from '../assets/Logo.jpeg';
import loginAnim from '../assets/login-animation.json';

const Auth = () => {
    const [isLogin, setIsLogin] = useState(true);
    const [formData, setFormData] = useState({ username: '', email: '', password: '' });
    const navigate = useNavigate();

    const handleAction = async (e) => {
        e.preventDefault();
        try {
            const endpoint = isLogin ? '/login' : '/register';
            const res = await axios.post(`http://localhost:5000/api/auth${endpoint}`, formData);
            if (res.data.user) {
                navigate('/dashboard', { state: { user: res.data.user } });
            }
        } catch (err) {
            alert(err.response?.data?.msg || "Action failed");
        }
    };

    return (
        <div className="min-h-screen flex items-center justify-center p-8 bg-[#0F172A]">
            <div className="w-full max-w-5xl flex flex-col md:flex-row items-center justify-around gap-16">

                {/* LEFT SIDE: Identity */}
                <div className="flex flex-col items-center space-y-4">
                    <img src={mascot} alt="Mascot" className="w-28 h-auto animate-float" />
                    <img src={logo} alt="Trellis Logo" className="h-12 w-auto" />
                    <p className="text-slate-400 text-sm font-medium tracking-wide">
                        Your peer-to-peer programming guardian.
                    </p>
                </div>

                {/* RIGHT SIDE: The Wireframe Glass Card */}
                <div className="glass-container p-12 rounded-[2rem] w-full max-w-md">
                    <h2 className="text-4xl font-bold text-white mb-6 text-center italic">
                        {isLogin ? 'Welcome Back!' : 'Create Account'}
                    </h2>

                    <div className="w-full h-16 flex justify-center mb-6 overflow-hidden">
                        <Lottie animationData={loginAnim} loop={true} className="h-24" />
                    </div>

                    <form onSubmit={handleAction} className="space-y-4">
                        {!isLogin && (
                            <input
                                type="text"
                                placeholder="Name"
                                className="w-full bg-slate-900/60 border border-white/10 rounded-lg py-3 px-4 text-white focus:outline-none focus:border-blue-500"
                                onChange={(e) => setFormData({ ...formData, username: e.target.value })}
                            />
                        )}
                        <input
                            type="email"
                            placeholder="Email"
                            required
                            className="w-full bg-slate-900/60 border border-white/10 rounded-lg py-3 px-4 text-white focus:outline-none focus:border-blue-500"
                            onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                        />
                        <input
                            type="password"
                            placeholder="Password"
                            required
                            className="w-full bg-slate-900/60 border border-white/10 rounded-lg py-3 px-4 text-white focus:outline-none focus:border-blue-500"
                            onChange={(e) => setFormData({ ...formData, password: e.target.value })}
                        />

                        <button type="submit" className="w-full bg-blue-600 hover:bg-blue-500 text-white font-black py-4 rounded-xl shadow-lg transition-transform active:scale-95 mt-4">
                            {isLogin ? 'LOG IN' : 'SIGN UP'}
                        </button>
                    </form>

                    <div className="mt-6 text-center">
                        <button
                            onClick={() => setIsLogin(!isLogin)}
                            className="text-slate-400 text-sm hover:text-white underline decoration-blue-500 underline-offset-4"
                        >
                            {isLogin ? "Need an account? Sign up" : "Already a member? Log in"}
                        </button>
                    </div>
                </div>

            </div>
        </div>
    );
};

export default Auth;
