import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
    Mail,
    Lock,
    User,
    ChevronRight,
    ChevronLeft,
    GraduationCap,
    Sparkles,
    BookOpen,
    Search,
    X
} from 'lucide-react';

const Register = () => {
    const [step, setStep] = useState(1);
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        password: '',
        major: '',
        year: '',
        skillsToLearn: [],
        skillsToTeach: []
    });
    const [currentSkill, setCurrentSkill] = useState('');
    const navigate = useNavigate();

    const addSkill = (type) => {
        if (!currentSkill.trim()) return;
        if (type === 'learn' && !formData.skillsToLearn.includes(currentSkill)) {
            setFormData({ ...formData, skillsToLearn: [...formData.skillsToLearn, currentSkill] });
        } else if (type === 'teach' && !formData.skillsToTeach.includes(currentSkill)) {
            setFormData({ ...formData, skillsToTeach: [...formData.skillsToTeach, currentSkill] });
        }
        setCurrentSkill('');
    };

    const removeSkill = (skill, type) => {
        if (type === 'learn') {
            setFormData({ ...formData, skillsToLearn: formData.skillsToLearn.filter(s => s !== skill) });
        } else {
            setFormData({ ...formData, skillsToTeach: formData.skillsToTeach.filter(s => s !== skill) });
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.post('http://localhost:5000/api/auth/register', formData);
            if (res.data.token) {
                localStorage.setItem('token', res.data.token);
                navigate('/dashboard', { state: { user: res.data.user } });
            }
        } catch (err) {
            alert(err.response?.data?.msg || "Registration failed");
        }
    };

    const nextStep = () => setStep(step + 1);
    const prevStep = () => setStep(step - 1);

    const steps = [
        {
            id: 1,
            title: "Basic Credentials",
            icon: <Mail className="w-6 h-6 text-indigo-400" />,
            content: (
                <div className="space-y-4">
                    <div className="relative">
                        <User className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-400 w-5 h-5" />
                        <input
                            type="text"
                            placeholder="Full Name"
                            className="glass-input pl-11"
                            value={formData.username}
                            onChange={(e) => setFormData({ ...formData, username: e.target.value })}
                            required
                        />
                    </div>
                    <div className="relative">
                        <Mail className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-400 w-5 h-5" />
                        <input
                            type="email"
                            placeholder="Email Address"
                            className="glass-input pl-11"
                            value={formData.email}
                            onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                            required
                        />
                    </div>
                    <div className="relative">
                        <Lock className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-400 w-5 h-5" />
                        <input
                            type="password"
                            placeholder="Strong Password"
                            className="glass-input pl-11"
                            value={formData.password}
                            onChange={(e) => setFormData({ ...formData, password: e.target.value })}
                            required
                        />
                    </div>
                </div>
            )
        },
        {
            id: 2,
            title: "Profile Setup",
            icon: <GraduationCap className="w-6 h-6 text-indigo-400" />,
            content: (
                <div className="space-y-4">
                    <div className="relative">
                        <BookOpen className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-400 w-5 h-5" />
                        <input
                            type="text"
                            placeholder="Major (e.g., Computer Science)"
                            className="glass-input pl-11"
                            value={formData.major}
                            onChange={(e) => setFormData({ ...formData, major: e.target.value })}
                            required
                        />
                    </div>
                    <div className="relative">
                        <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-400 w-5 h-5" />
                        <select
                            className="glass-input pl-11 appearance-none"
                            value={formData.year}
                            onChange={(e) => setFormData({ ...formData, year: e.target.value })}
                            required
                        >
                            <option value="" disabled className="bg-slate-900">Select Year</option>
                            <option value="Freshman" className="bg-slate-900">Freshman</option>
                            <option value="Sophomore" className="bg-slate-900">Sophomore</option>
                            <option value="Junior" className="bg-slate-900">Junior</option>
                            <option value="Senior" className="bg-slate-900">Senior</option>
                        </select>
                    </div>
                </div>
            )
        },
        {
            id: 3,
            title: "The Trellis Spark",
            icon: <Sparkles className="w-6 h-6 text-indigo-400" />,
            content: (
                <div className="space-y-6">
                    <div className="space-y-2">
                        <label className="text-sm text-slate-400">Goals: What do you want to learn?</label>
                        <div className="flex gap-2">
                            <input
                                type="text"
                                placeholder="Add skill (e.g., React)"
                                className="glass-input flex-1"
                                value={currentSkill}
                                onChange={(e) => setCurrentSkill(e.target.value)}
                                onKeyPress={(e) => e.key === 'Enter' && addSkill('learn')}
                            />
                            <button
                                type="button"
                                onClick={() => addSkill('learn')}
                                className="px-4 py-2 bg-indigo-600 rounded-xl hover:bg-indigo-500 transition-colors"
                            >
                                Add
                            </button>
                        </div>
                        <div className="flex flex-wrap gap-2 mt-2">
                            {formData.skillsToLearn.map((skill, i) => (
                                <span key={i} className="flex items-center gap-1 px-3 py-1 bg-indigo-500/20 border border-indigo-500/50 rounded-full text-xs text-indigo-300">
                                    {skill}
                                    <X className="w-3 h-3 cursor-pointer" onClick={() => removeSkill(skill, 'learn')} />
                                </span>
                            ))}
                        </div>
                    </div>

                    <div className="space-y-2">
                        <label className="text-sm text-slate-400">Strengths: What can you teach?</label>
                        <div className="flex gap-2">
                            <input
                                type="text"
                                placeholder="Add skill (e.g., Python)"
                                className="glass-input flex-1"
                                value={currentSkill}
                                onChange={(e) => setCurrentSkill(e.target.value)}
                                onKeyPress={(e) => e.key === 'Enter' && addSkill('teach')}
                            />
                            <button
                                type="button"
                                onClick={() => addSkill('teach')}
                                className="px-4 py-2 bg-emerald-600 rounded-xl hover:bg-emerald-500 transition-colors"
                            >
                                Add
                            </button>
                        </div>
                        <div className="flex flex-wrap gap-2 mt-2">
                            {formData.skillsToTeach.map((skill, i) => (
                                <span key={i} className="flex items-center gap-1 px-3 py-1 bg-emerald-500/20 border border-emerald-500/50 rounded-full text-xs text-emerald-300">
                                    {skill}
                                    <X className="w-3 h-3 cursor-pointer" onClick={() => removeSkill(skill, 'teach')} />
                                </span>
                            ))}
                        </div>
                    </div>
                </div>
            )
        }
    ];

    return (
        <div className="min-h-screen flex flex-col items-center justify-center p-6 bg-[#0F172A]">
            <div className="w-full max-w-xl">
                {/* Progress Bar */}
                <div className="flex justify-between mb-8 relative">
                    <div className="absolute top-1/2 left-0 w-full h-0.5 bg-slate-800 -translate-y-1/2 z-0" />
                    <div
                        className="absolute top-1/2 left-0 h-0.5 bg-indigo-500 -translate-y-1/2 z-0 transition-all duration-500"
                        style={{ width: `${((step - 1) / (steps.length - 1)) * 100}%` }}
                    />
                    {steps.map((s) => (
                        <div
                            key={s.id}
                            className={`relative z-10 flex flex-col items-center gap-2`}
                        >
                            <div className={`w-10 h-10 rounded-full flex items-center justify-center transition-all duration-500 border-2 ${step >= s.id ? 'bg-indigo-500 border-indigo-400 shadow-[0_0_15px_rgba(99,102,241,0.5)]' : 'bg-slate-900 border-slate-700'
                                }`}>
                                {step > s.id ? <ChevronRight className="w-6 h-6" /> : s.icon}
                            </div>
                        </div>
                    ))}
                </div>

                <div className="glass-card p-10 mt-8">
                    <h2 className="text-3xl font-bold text-white mb-2">{steps[step - 1].title}</h2>
                    <p className="text-slate-400 mb-8 text-sm">Join the Trellis e-learning community.</p>

                    <form onSubmit={step === steps.length ? handleSubmit : (e) => e.preventDefault()}>
                        <AnimatePresence mode="wait">
                            <motion.div
                                key={step}
                                initial={{ x: 20, opacity: 0 }}
                                animate={{ x: 0, opacity: 1 }}
                                exit={{ x: -20, opacity: 0 }}
                                transition={{ duration: 0.3 }}
                            >
                                {steps[step - 1].content}
                            </motion.div>
                        </AnimatePresence>

                        <div className="flex gap-4 mt-10">
                            {step > 1 && (
                                <button
                                    type="button"
                                    onClick={prevStep}
                                    className="flex-1 px-6 py-4 rounded-full border border-slate-700 text-slate-400 font-bold hover:bg-slate-800 transition-colors flex items-center justify-center gap-2"
                                >
                                    <ChevronLeft className="w-5 h-5" /> Back
                                </button>
                            )}
                            {step < steps.length ? (
                                <button
                                    type="button"
                                    onClick={nextStep}
                                    className="btn-blue flex-1 flex items-center justify-center gap-2"
                                >
                                    Continue <ChevronRight className="w-5 h-5" />
                                </button>
                            ) : (
                                <button
                                    type="submit"
                                    className="btn-blue flex-1 shadow-[0_0_20px_rgba(59,130,246,0.4)]"
                                >
                                    Launch Your Spark
                                </button>
                            )}
                        </div>
                    </form>

                    <p className="text-center mt-8 text-slate-500 text-sm">
                        Already have an account?{' '}
                        <button
                            onClick={() => navigate('/login')}
                            className="text-indigo-400 hover:text-indigo-300 font-medium underline underline-offset-4"
                        >
                            Log In
                        </button>
                    </p>
                </div>
            </div>
        </div>
    );
};

export default Register;
