import React from 'react';
import { motion } from 'framer-motion';
import {
    Layout,
    Zap,
    Trophy,
    Target,
    BookOpen,
    Settings,
    ChevronRight,
    ChevronLeft,
    Lock,
    Eye,
    Star,
    Flame,
    Crown,
    Map
} from 'lucide-react';
import YipHead from '../assets/Yip_head.png';
import mascot from '../assets/trellis_mascot.png';

const Dashboard = () => {
    // Simulated data
    const dailyGoals = [
        { id: 1, title: 'Earn 90 XP', current: 0, total: 90, icon: <Zap className="w-5 h-5 text-orange-400" /> },
        { id: 2, title: 'Complete 3 exercises', current: 0, total: 3, icon: <BookOpen className="w-5 h-5 text-blue-400" /> },
        { id: 3, title: 'Solve 2 challenges on the first try', current: 0, total: 2, icon: <Target className="w-5 h-5 text-red-400" /> }
    ];

    const learningPath = [
        { id: 1, title: "What is HTML?", status: "current" },
        { id: 2, title: "Tags and Elements", status: "locked" },
        { id: 3, title: "Basic Structure of HTML", status: "locked" },
        { id: 4, title: "Attributes & Values", status: "locked" },
        { id: 5, title: "Lists & Tables", status: "locked" },
        { id: 6, title: "Forms & Inputs", status: "locked" }
    ];

    return (
        <div className="flex min-h-screen bg-[#111827] text-white font-['Inter',sans-serif]">

            {/* LEFT SIDEBAR: Navigation */}
            <aside className="w-20 md:w-32 fixed left-0 top-0 bottom-0 bg-[#1f2937] border-r border-white/5 flex flex-col items-center py-8 z-50">
                <div className="mb-12 px-1 transition-transform hover:scale-110">
                    <img src={YipHead} alt="Yip" className="w-full h-auto drop-shadow-[0_4px_20px_rgba(74,144,226,0.7)]" />
                </div>

                <nav className="flex flex-col gap-8">
                    <NavItem icon={<Map className="w-8 h-8" />} active />
                    <NavItem icon={<Zap className="w-8 h-8" />} />
                    <NavItem icon={<Trophy className="w-8 h-8" />} />
                    <NavItem icon={<BookOpen className="w-8 h-8" />} />
                    <NavItem icon={<Target className="w-8 h-8" />} />
                </nav>

                <div className="mt-auto">
                    <NavItem icon={<Settings className="w-9 h-9" />} />
                </div>
            </aside>

            {/* MAIN CONTENT AREA */}
            <main className="flex-1 ml-20 md:ml-32 mr-0 lg:mr-[350px] p-6 lg:p-10 flex flex-col items-center">

                {/* Learning Path Header */}
                <div className="w-full max-w-3xl bg-[#1e293b] rounded-2xl p-6 mb-12 relative overflow-hidden shadow-2xl border border-white/5">
                    <div className="flex items-center gap-4 text-slate-400 text-sm font-bold uppercase tracking-widest mb-2">
                        <ChevronLeft className="w-4 h-4" />
                        <span>Section 1, Chapter 1</span>
                    </div>
                    <h1 className="text-3xl font-black italic tracking-tight">HTML Basics</h1>
                    <div className="absolute top-0 right-0 w-32 h-32 bg-blue-500/10 blur-[60px] rounded-full -mr-16 -mt-16" />
                </div>

                {/* Vertical Path */}
                <div className="flex flex-col items-center gap-6 relative pb-20">
                    {/* Connection Line */}
                    <div className="absolute top-0 bottom-0 left-1/2 -translate-x-1/2 w-1.5 bg-slate-800" />

                    {learningPath.map((node, index) => (
                        <PathNode key={node.id} node={node} index={index} />
                    ))}
                </div>
            </main>

            {/* RIGHT SIDEBAR: Stats & Goals */}
            <aside className="hidden lg:flex w-[350px] fixed right-0 top-0 bottom-0 bg-[#111827] border-l border-white/5 flex-col p-8 pt-20 gap-8 invisible-scrollbar overflow-y-auto">

                {/* Stats Bar */}
                <div className="flex justify-between items-center bg-[#1f2937] p-4 rounded-2xl border border-white/5 shadow-lg relative">
                    <StatItem icon={<span className="text-xl font-black text-orange-400">5</span>} label="HTML Mastery" value="5">
                        <JourneysPopover />
                    </StatItem>
                    <StatItem icon={<Flame className="w-6 h-6 text-orange-500" />} label="Daily Streak" value="0">
                        <StreakPopover />
                    </StatItem>
                </div>

                {/* Leaderboard Section */}
                <div className="bg-[#1f2937] rounded-3xl p-6 border border-white/5 shadow-xl">
                    <div className="flex justify-between items-center mb-6">
                        <h3 className="text-xl font-bold italic uppercase tracking-tighter">Leaderboard</h3>
                        <button className="text-blue-400 text-sm font-bold hover:underline">View</button>
                    </div>
                    <div className="flex flex-col items-center py-8 bg-[#2d3748]/50 rounded-2xl border border-white/5">
                        <div className="w-20 h-20 bg-slate-700/50 rounded-2xl flex items-center justify-center mb-4 relative overflow-hidden">
                            <Lock className="w-8 h-8 text-slate-500" />
                            <div className="absolute inset-0 bg-gradient-to-t from-black/20 to-transparent" />
                        </div>
                        <p className="text-slate-400 text-center text-sm font-medium px-6 leading-relaxed">
                            Reach <span className="text-white font-bold">100 XP</span> to unlock<br />leaderboards!
                        </p>
                    </div>
                </div>

                {/* Daily Goals Section */}
                <div className="bg-[#1f2937] rounded-3xl p-6 border border-white/5 shadow-xl">
                    <div className="flex justify-between items-center mb-6">
                        <h3 className="text-xl font-bold italic uppercase tracking-tighter">Daily Goals</h3>
                        <button className="text-blue-400 text-sm font-bold hover:underline">View</button>
                    </div>

                    <div className="space-y-6">
                        {dailyGoals.map(goal => (
                            <div key={goal.id} className="space-y-2">
                                <div className="flex justify-between items-center">
                                    <span className="text-sm font-bold text-slate-300">{goal.title}</span>
                                    <span className="text-xs font-black text-slate-500">{goal.current}/{goal.total}</span>
                                </div>
                                <div className="h-3 w-full bg-slate-800 rounded-full overflow-hidden relative">
                                    <div
                                        className={`h-full bg-gradient-to-r from-blue-500 to-indigo-500 rounded-full`}
                                        style={{ width: `${(goal.current / goal.total) * 100}%` }}
                                    />
                                    {/* Treasure Box Icon at end of bar if needed, but for now simple bar */}
                                </div>
                            </div>
                        ))}
                    </div>
                </div>

                {/* Branding footer */}
                <div className="mt-auto pt-6 text-center">
                    <p className="text-[10px] text-slate-600 font-bold uppercase tracking-[0.2em] flex items-center justify-center gap-4">
                        <span>About</span>
                        <span>Certifications</span>
                        <span>Affiliate</span>
                    </p>
                </div>
            </aside>
        </div>
    );
};

const NavItem = ({ icon, active = false }) => (
    <button className={`w-12 h-12 flex items-center justify-center rounded-2xl transition-all ${active ? 'bg-blue-600 text-white shadow-lg shadow-blue-600/30' : 'text-slate-400 hover:bg-white/5 hover:text-white'
        }`}>
        {icon}
    </button>
);

const StatItem = ({ icon, value, label, children }) => (
    <div className="flex flex-col items-center gap-2 p-3 bg-slate-800/40 rounded-2xl border border-white/5 group relative cursor-help flex-1 min-w-[100px] transition-all hover:bg-slate-800/60">
        {icon}
        <span className="text-sm font-black text-slate-300">{value}</span>

        {/* Popover/Tooltip Content (Now on TOP) */}
        <div className="absolute bottom-[calc(100%+12px)] left-1/2 -translate-x-1/2 opacity-0 group-hover:opacity-100 transition-opacity z-50 pointer-events-none group-hover:pointer-events-auto">
            {children ? children : (
                <div className="bg-[#2d3748] text-white text-[10px] font-bold py-1 px-3 rounded-lg whitespace-nowrap shadow-xl border border-white/5">
                    {label}
                </div>
            )}
            {/* Triangle tail for simpler tooltips if no children provided */}
            {!children && <div className="absolute -bottom-1 left-1/2 -translate-x-1/2 w-2 h-2 bg-[#2d3748] rotate-45 border-b border-r border-white/5" />}
        </div>
    </div>
);

const JourneysPopover = () => (
    <div className="w-[280px] bg-[#1f2937] rounded-3xl overflow-hidden shadow-2xl border border-white/10 p-2 transform origin-bottom -translate-y-2">
        <div className="p-4 border-b border-white/5">
            <h4 className="text-slate-300 font-bold text-sm tracking-tight opacity-70">My Journeys</h4>
        </div>

        <div className="p-2 space-y-1">
            {/* Current Journey */}
            <button className="w-full flex items-center gap-4 p-3 bg-white/5 rounded-2xl border border-white/10 hover:bg-white/10 transition-all text-left group/item">
                <div className="w-10 h-10 bg-orange-600 rounded-xl flex items-center justify-center font-black text-xl italic shadow-lg shadow-orange-900/20">5</div>
                <span className="font-black italic uppercase text-lg tracking-tight text-white group-hover/item:scale-105 transition-transform">HTML</span>
            </button>

            {/* Add Journey */}
            <button className="w-full flex items-center gap-4 p-3 hover:bg-white/5 rounded-2xl transition-all text-left group/add text-slate-400 hover:text-white">
                <div className="w-10 h-10 border-2 border-dashed border-slate-700 rounded-xl flex items-center justify-center group-hover/add:border-blue-500/50 group-hover/add:bg-blue-500/10">
                    <Map className="w-5 h-5" />
                </div>
                <span className="font-bold text-base tracking-tight italic uppercase opacity-60 group-hover/add:opacity-100">Add Journey</span>
            </button>
        </div>

        {/* Triangle tail pointing DOWN at the icon */}
        <div className="absolute -bottom-1.5 left-1/2 -translate-x-1/2 w-3 h-3 bg-[#1f2937] rotate-45 border-b border-r border-white/10" />
    </div>
);

const StreakPopover = () => (
    <div className="w-[300px] bg-[#1f2937] rounded-[2rem] overflow-hidden shadow-2xl border border-white/5 transform origin-bottom -translate-y-2">
        {/* Header Section */}
        <div className="bg-[#92400e] p-6 text-left relative overflow-hidden">
            <h4 className="text-2xl font-black italic uppercase tracking-tighter mb-1 relative z-10">0 day streak</h4>
            <p className="text-orange-100/70 text-[11px] font-bold leading-tight max-w-[150px] relative z-10">
                Do a lesson today to start a new streak!
            </p>
            {/* Large background flame */}
            <Flame className="absolute -right-4 -bottom-4 w-32 h-32 text-orange-600/20 rotate-12" />
            <Flame className="absolute right-6 top-1/2 -translate-y-1/2 w-16 h-16 text-white/20" />
        </div>

        {/* Content Section */}
        <div className="p-6 space-y-6">
            {/* Week Progress */}
            <div className="flex justify-between items-center px-1">
                {['S', 'M', 'T', 'W', 'T', 'F', 'S'].map((day, i) => (
                    <div key={i} className="flex flex-col items-center gap-2">
                        <span className="text-[10px] font-black text-slate-500">{day}</span>
                        <div className={`w-8 h-8 rounded-full border-2 flex items-center justify-center ${i === 6 ? 'border-slate-700' : 'border-slate-800 bg-slate-800/50'}`}>
                            {i === 6 && <Star className="w-4 h-4 text-slate-700" />}
                        </div>
                    </div>
                ))}
            </div>

            {/* Streak Club Box */}
            <div className="bg-[#111827] rounded-2xl p-4 border border-white/5 flex gap-4 items-start">
                <div className="w-12 h-12 rounded-xl bg-slate-800 flex items-center justify-center shrink-0">
                    <Lock className="w-6 h-6 text-slate-600" />
                </div>
                <div className="space-y-3 flex-1">
                    <div>
                        <h5 className="text-xs font-bold text-white mb-1 uppercase tracking-tight">Streak Club</h5>
                        <p className="text-[10px] text-slate-500 font-medium leading-relaxed">
                            Reach a 7 day streak to join a streak club and earn exclusive perks.
                        </p>
                    </div>
                    {/* Progress Bar */}
                    <div className="h-2 w-full bg-slate-800 rounded-full relative">
                        <div className="absolute right-0 top-1/2 -translate-y-1/2 bg-[#111827] p-1 rounded-full border border-slate-700">
                            <Flame className="w-2 h-2 text-slate-700" />
                        </div>
                    </div>
                </div>
            </div>

            {/* View More Button */}
            <button className="w-full py-3 bg-[#0891b2] hover:bg-[#0e7490] text-white font-black italic uppercase text-sm rounded-xl transition-all shadow-lg shadow-cyan-900/20">
                View More
            </button>
        </div>

        {/* Triangle tail pointing DOWN at the icon */}
        <div className="absolute -bottom-1.5 left-1/2 -translate-x-1/2 w-3 h-3 bg-[#1f2937] rotate-45 border-b border-r border-white/10" />
    </div>
);

const PathNode = ({ node, index }) => {
    const [isHovered, setIsHovered] = React.useState(false);

    return (
        <motion.div
            initial={{ scale: 0.8, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            transition={{ delay: index * 0.1 }}
            className="relative z-10"
            onMouseEnter={() => setIsHovered(true)}
            onMouseLeave={() => setIsHovered(false)}
        >
            <div className={`w-28 h-28 rounded-[2rem] flex items-center justify-center border-4 transition-all transform hover:scale-105 cursor-pointer ${node.status === 'current'
                ? 'bg-[#1e4e6e] border-blue-400 shadow-[0_0_40px_rgba(96,165,250,0.3)]'
                : 'bg-[#1f2937] border-slate-800'
                }`}>
                {node.status === 'current' && (
                    <div className="absolute -top-12 bg-[#2d3748] border border-white/10 px-6 py-2 rounded-xl shadow-2xl z-20">
                        <span className="text-white font-black italic uppercase text-lg tracking-tight">START</span>
                        <div className="absolute -bottom-1.5 left-1/2 -translate-x-1/2 w-3 h-3 bg-[#2d3748] border-r border-b border-white/10 rotate-45" />
                    </div>
                )}

                {node.status === 'current' ? (
                    <div className="w-16 h-16 bg-blue-500 rounded-full flex items-center justify-center shadow-inner">
                        <Zap className="w-8 h-8 text-white fill-current" />
                    </div>
                ) : (
                    <Lock className="w-8 h-8 text-slate-700" />
                )}
            </div>

            {/* Lesson Name Tooltip on Hover (Now on TOP) */}
            {isHovered && (
                <motion.div
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="absolute bottom-[calc(100%+16px)] left-1/2 -translate-x-1/2 bg-[#2d3748] border border-white/5 px-6 py-4 rounded-2xl shadow-2xl z-50 text-center min-w-[220px]"
                >
                    <h4 className="text-white font-black italic uppercase text-sm mb-1">{node.title}</h4>
                    <p className="text-slate-400 text-[10px] font-bold uppercase tracking-widest leading-relaxed">
                        {node.status === 'locked' ? 'Complete the lesson above to unlock this!' : 'Begin your journey!'}
                    </p>
                    {node.status === 'locked' && (
                        <div className="mt-3 w-max mx-auto px-4 py-1.5 bg-slate-700/50 rounded-xl text-[10px] font-black text-slate-500 uppercase">
                            LOCKED
                        </div>
                    )}
                    <div className="absolute -bottom-1.5 left-1/2 -translate-x-1/2 w-3 h-3 bg-[#2d3748] border-b border-r border-white/5 rotate-45" />
                </motion.div>
            )}
        </motion.div>
    );
};

export default Dashboard;
