import React, { useState, useRef } from 'react';
import Lottie from 'lottie-react';
import animationData from './assets/login-animation.json';
import logo from './assets/Logo.jpeg';
import './Login.css';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const lottieRef = useRef(null);

    const handleInputFocus = (inputType) => {
        if (lottieRef.current) {
            if (inputType === 'password') {
                lottieRef.current.playSegments([30, 60], true);
            } else {
                lottieRef.current.playSegments([0, 30], true);
            }
        }
    };

    return (
        <div className="login-container">
            <div className="login-card">
                <div className="logo-section">
                    <img src={logo} alt="Trellis Logo" className="brand-logo-white" />
                </div>
                
                <div className="visual-section">
                    <Lottie 
                        lottieRef={lottieRef}
                        animationData={animationData} 
                        loop={true} 
                        style={{ height: 180 }}
                    />
                </div>

                <form className="login-form">
                    <h2 className="welcome-text">Sign In</h2>
                    <input 
                        type="email" 
                        placeholder="Email Address" 
                        onFocus={() => handleInputFocus('email')}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    <input 
                        type="password" 
                        placeholder="Password" 
                        onFocus={() => handleInputFocus('password')}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button type="submit" className="login-btn">Enter System</button>
                </form>
            </div>
        </div>
    );
};

export default Login;
