import React from 'react';
import { useLocation } from 'react-router-dom';
import { BookOpen, Users, Zap } from 'lucide-react';
import mascot from '../assets/trellis_mascot.png';

const Dashboard = () => {
    const { state } = useLocation();
    const user = state?.user || { username: "Scholar", progress: 0 };

    return (
        <div className="min-h-screen p-8 max-w-6xl mx-auto space-y-12">
            {/* Header */}
            <section className="glass-card rounded-[3rem] p-10 flex flex-col md:flex-row items-center gap-8">
                <img src={mascot} alt="Mascot" className="w-40 animate-bounce-slow" />
                <div className="flex-grow space-y-4">
                    <h2 className="text-5xl font-black text-white italic">Welcome back, {user.username}!</h2>
                    <div className="space-y-2 max-w-md">
                        <div className="flex justify-between text-sm font-bold text-blue-400 uppercase tracking-widest">
                            <span>S6 Progress</span>
                            <span>{user.progress}%</span>
                        </div>
                        <div className="w-full bg-slate-800 rounded-full h-3">
                            <div className="bg-blue-500 h-full rounded-full" style={{ width: `${user.progress}%` }}></div>
                        </div>
                    </div>
                </div>
            </section>

            {/* Buttons */}
            <section className="grid grid-cols-1 md:grid-cols-3 gap-6">
                {[
                    { name: "MATCHMAKING AI", icon: <Users />, color: "bg-purple-600" },
                    { name: "RESOURCES", icon: <BookOpen />, color: "bg-blue-600" },
                    { name: "LIVE ROOMS", icon: <Zap />, color: "bg-emerald-600" }
                ].map(item => (
                    <button key={item.name} className="glass-card p-8 rounded-[2rem] text-left hover:bg-white/10 transition-all group">
                        <div className={`${item.color} w-12 h-12 rounded-xl flex items-center justify-center mb-4 group-hover:scale-110 transition-transform`}>
                            {item.icon}
                        </div>
                        <h3 className="font-black text-xl">{item.name}</h3>
                    </button>
                ))}
            </section>
        </div>
    );
};

export default Dashboard;
