import React, { useState } from 'react';
import Lottie from 'lottie-react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

// Import assets - ensure these names are exact in your folder
import mascot from '../assets/trellis_mascot.png';
import logo from '../assets/Logo.jpeg';
import loginAnim from '../assets/login-animation.json';

const Auth = () => {
    const [isLogin, setIsLogin] = useState(true);
    const [formData, setFormData] = useState({ username: '', email: '', password: '' });
    const navigate = useNavigate();

    const handleAction = async (e) => {
        e.preventDefault();
        try {
            const endpoint = isLogin ? '/login' : '/register';
            const res = await axios.post(`http://localhost:5000/api/auth${endpoint}`, formData);
            if (res.data.user) {
                navigate('/dashboard', { state: { user: res.data.user } });
            }
        } catch (err) {
            alert(err.response?.data?.msg || "Error occurred");
        }
    };

    return (
        <div className="min-h-screen w-full flex items-center justify-center p-6 bg-[#0F172A]">
            <div className="w-full max-w-5xl flex flex-col md:flex-row items-center justify-center gap-20">

                {/* LEFT SECTION: Logo and Mascot (Small) */}
                <div className="flex flex-col items-center space-y-6">
                    <img
                        src={mascot}
                        alt="Mascot"
                        className="w-24 h-auto drop-shadow-lg"
                    />
                    <img
                        src={logo}
                        alt="Trellis Logo"
                        className="h-10 w-auto object-contain"
                    />
                    <p className="text-slate-400 text-sm font-light text-center">
                        Your peer-to-peer programming guardian.
                    </p>
                </div>

                {/* RIGHT SECTION: The Glass Auth Card */}
                <div className="glass-panel p-12 w-full max-w-md">
                    <h2 className="text-4xl font-bold text-white mb-8 text-center tracking-tight">
                        {isLogin ? 'Welcome Back!' : 'Create Account'}
                    </h2>

                    <form onSubmit={handleAction} className="space-y-5">
                        {/* Lottie Animation (Small, above button) */}
                        <div className="w-full h-16 flex justify-center items-center overflow-hidden mb-2">
                            <Lottie
                                animationData={loginAnim}
                                loop={true}
                                className="h-20 opacity-90"
                            />
                        </div>

                        {!isLogin && (
                            <input
                                type="text"
                                placeholder="Name"
                                className="w-full bg-slate-900/40 border border-white/5 rounded-xl py-3 px-4 text-white outline-none focus:border-blue-500 transition-all"
                                onChange={(e) => setFormData({ ...formData, username: e.target.value })}
                            />
                        )}

                        <input
                            type="email"
                            placeholder="Email"
                            required
                            className="w-full bg-slate-900/40 border border-white/5 rounded-xl py-3 px-4 text-white outline-none focus:border-blue-500 transition-all"
                            onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                        />

                        <input
                            type="password"
                            placeholder="Password"
                            required
                            className="w-full bg-slate-900/40 border border-white/5 rounded-xl py-3 px-4 text-white outline-none focus:border-blue-500 transition-all"
                            onChange={(e) => setFormData({ ...formData, password: e.target.value })}
                        />

                        <button type="submit" className="wireframe-button mt-4">
                            {isLogin ? 'LOG IN' : 'SIGN UP'}
                        </button>
                    </form>

                    <div className="mt-8 text-center">
                        <button
                            onClick={() => setIsLogin(!isLogin)}
                            className="text-slate-400 text-xs hover:text-white transition-all underline underline-offset-4"
                        >
                            {isLogin ? "Need an account? Sign up" : "Already have an account? Log in"}
                        </button>
                    </div>
                </div>

            </div>
        </div>
    );
};

export default Auth;
