import React from 'react';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import {
    ChevronLeft,
    ChevronRight,
    Lock,
    Zap,
    Trophy,
    Target,
    BookOpen,
    Settings,
    Map,
    Flame,
    Star,
    Award
} from 'lucide-react';
import YipHead from '../assets/Yip_head.png';
import { courseData } from '../data/courses';

const Sections = () => {
    const navigate = useNavigate();
    const activeCourseId = localStorage.getItem('selectedCourse') || 'html';
    const activeCourse = courseData[activeCourseId] || courseData.html;

    const handleJumpToSection = (sectionId) => {
        localStorage.setItem('selectedSectionId', sectionId);
        navigate('/dashboard');
    };

    return (
        <div className="flex min-h-screen bg-[#111827] text-white font-['Inter',sans-serif]">

            {/* LEFT SIDEBAR: Navigation (Compact) */}
            <aside className="w-16 md:w-24 fixed left-0 top-0 bottom-0 bg-[#1f2937] border-r border-white/5 flex flex-col items-center py-6 z-50">
                <div className="mb-8 px-2 transition-transform hover:scale-110 cursor-pointer" onClick={() => navigate('/dashboard')}>
                    <img src={YipHead} alt="Yip" className="w-full h-auto drop-shadow-[0_4px_15px_rgba(74,144,226,0.5)]" />
                </div>

                <nav className="flex flex-col gap-6">
                    <NavItem icon={<Map className="w-6 h-6" />} onClick={() => navigate('/dashboard')} active />
                    <NavItem icon={<Zap className="w-6 h-6" />} />
                    <NavItem icon={<Trophy className="w-6 h-6" />} />
                    <NavItem icon={<BookOpen className="w-6 h-6" />} />
                    <NavItem icon={<Target className="w-6 h-6" />} />
                </nav>

                <div className="mt-auto">
                    <NavItem icon={<Settings className="w-7 h-7" />} />
                </div>
            </aside>

            {/* MAIN CONTENT AREA */}
            <main className="flex-1 ml-16 md:ml-24 mr-0 lg:mr-[300px] p-8 lg:p-12 overflow-y-auto h-screen invisible-scrollbar">
                <div className="max-w-4xl mx-auto space-y-10">

                    {/* Back Header */}
                    <button
                        onClick={() => navigate('/journeys')}
                        className="flex items-center gap-2 text-slate-400 hover:text-white transition-colors font-black uppercase text-sm group"
                    >
                        <ChevronLeft className="w-4 h-4 group-hover:-translate-x-1 transition-transform" />
                        <span>Back</span>
                    </button>

                    {/* Course Header */}
                    <div className="space-y-2">
                        <h1 className="text-4xl font-black italic uppercase tracking-tighter text-white">
                            {activeCourse.title}
                        </h1>
                        <div className="h-1.5 w-20 bg-blue-600 rounded-full" />
                    </div>

                    {/* Sections List */}
                    <div className="space-y-6">
                        {activeCourse.sections.map((section, idx) => (
                            <motion.div
                                key={section.id}
                                initial={{ opacity: 0, y: 20 }}
                                animate={{ opacity: 1, y: 0 }}
                                transition={{ delay: idx * 0.1 }}
                                className="bg-[#1f2937]/50 border border-white/5 rounded-[2.5rem] p-8 relative overflow-hidden group hover:bg-[#1f2937] transition-all"
                            >
                                <div className="relative z-10 flex flex-col md:flex-row md:items-center justify-between gap-8">
                                    <div className="space-y-4 max-w-xl">
                                        <div className="space-y-1">
                                            <span className="text-blue-500 font-bold text-sm uppercase tracking-widest">Section {section.id}</span>
                                            <h3 className="text-2xl font-black italic uppercase tracking-tight text-white">{section.title}</h3>
                                        </div>
                                        <p className="text-slate-400 font-medium leading-relaxed">
                                            {section.description}
                                        </p>

                                        {/* Progress Bar Placeholder */}
                                        <div className="space-y-2 pt-2">
                                            <div className="flex justify-between items-center text-[10px] font-black uppercase tracking-widest text-slate-500/80">
                                                <span>0 / {section.lessons.length} lessons</span>
                                                <span>0%</span>
                                            </div>
                                            <div className="h-2 w-full bg-slate-800/50 rounded-full overflow-hidden">
                                                <div className="h-full w-0 bg-blue-600 rounded-full" />
                                            </div>
                                        </div>
                                    </div>

                                    <div className="flex items-center gap-4">
                                        <div className="p-3 bg-white/5 rounded-2xl border border-white/10 opacity-40">
                                            <Award className="w-6 h-6 text-slate-400" />
                                        </div>
                                        <button
                                            onClick={() => handleJumpToSection(section.id)}
                                            className="px-8 py-4 bg-slate-800/80 hover:bg-blue-600 text-slate-400 hover:text-white font-black italic uppercase text-sm rounded-2xl transition-all active:scale-95 border-2 border-white/5 hover:border-blue-500/30 shadow-xl"
                                        >
                                            Jump to Section
                                        </button>
                                    </div>
                                </div>

                                {/* Background Decorations */}
                                <div className="absolute top-0 right-0 w-64 h-64 bg-blue-500/5 blur-[80px] rounded-full -mr-32 -mt-32 pointer-events-none" />
                                <div className={`absolute bottom-0 left-0 w-32 h-32 bg-blue-600/5 blur-[50px] rounded-full -ml-16 -mb-16 pointer-events-none opacity-0 group-hover:opacity-100 transition-opacity`} />
                            </motion.div>
                        ))}
                    </div>
                </div>
            </main>

            {/* RIGHT SIDEBAR: Stats (Mirroring Dashboard for consistency) */}
            <aside className="hidden lg:flex w-[300px] fixed right-0 top-0 bottom-0 bg-[#111827] border-l border-white/5 flex-col p-6 gap-6 invisible-scrollbar overflow-y-auto">
                <div className="flex justify-between items-center bg-[#1f2937] p-3 rounded-xl border border-white/5 shadow-lg">
                    <StatItem value="5" />
                    <StatItem value="0" />
                </div>

                <div className="bg-[#1f2937] rounded-2xl p-5 border border-white/5 shadow-xl">
                    <h3 className="text-lg font-bold italic uppercase tracking-tighter mb-4 text-slate-500/50">LEADERBOARD</h3>
                    <div className="flex flex-col items-center py-5 bg-[#2d3748]/50 rounded-xl border border-white/5">
                        <Lock className="w-6 h-6 text-slate-500 opacity-20" />
                    </div>
                </div>

                <div className="bg-[#1f2937] rounded-2xl p-5 border border-white/5 shadow-xl">
                    <h3 className="text-lg font-bold italic uppercase tracking-tighter mb-4">DAILY GOALS</h3>
                    <div className="space-y-4">
                        <GoalItem title="Earn 90 XP" />
                        <GoalItem title="Complete 3 exercises" />
                    </div>
                </div>
            </aside>
        </div>
    );
};

const NavItem = ({ icon, active = false, onClick }) => (
    <button
        onClick={onClick}
        className={`w-10 h-10 flex items-center justify-center rounded-xl transition-all ${active ? 'bg-blue-600 text-white shadow-lg shadow-blue-600/30' : 'text-slate-400 hover:bg-white/5 hover:text-white'
            }`}>
        {icon}
    </button>
);

const StatItem = ({ value }) => (
    <div className="flex flex-col items-center gap-0.5 p-2 bg-slate-800/40 rounded-xl border border-white/5 flex-1 min-w-[80px]">
        <div className="w-8 h-8 flex items-center justify-center">
            {value === "5" ? (
                <div className="w-7 h-7 bg-orange-600 rounded-md flex items-center justify-center font-black text-sm italic text-white">5</div>
            ) : (
                <Flame className="w-6 h-6 text-orange-500" />
            )}
        </div>
        <span className="text-[10px] font-black text-slate-300">{value}</span>
    </div>
);

const GoalItem = ({ title }) => (
    <div className="space-y-1.5">
        <div className="flex justify-between items-center">
            <span className="text-xs font-bold text-slate-300">{title}</span>
            <span className="text-[10px] font-black text-slate-500">0/1</span>
        </div>
        <div className="h-2 w-full bg-slate-800 rounded-full overflow-hidden">
            <div className="h-full w-0 bg-blue-600" />
        </div>
    </div>
);

export default Sections;
