import React, { useState } from 'react';
import Lottie from 'lottie-react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Mail, Lock, User, ArrowRight, Sparkles } from 'lucide-react';

import mascot from '../assets/trellis_mascot.png';
import authAnimation from '../assets/login-animation.json';

const Auth = () => {
    const [isLogin, setIsLogin] = useState(true);
    const [formData, setFormData] = useState({ username: '', email: '', password: '' });
    const navigate = useNavigate();

    const handleAction = async (e) => {
        e.preventDefault();
        const baseUrl = "http://localhost:5000/api/auth";
        const endpoint = isLogin ? '/login' : '/register';

        try {
            const res = await axios.post(`${baseUrl}${endpoint}`, formData);
            if (res.data.user) {
                navigate('/dashboard', { state: { user: res.data.user } });
            }
        } catch (err) {
            alert(err.response?.data?.msg || "Authentication failed.");
        }
    };

    return (
        <div className="min-h-screen bg-[#0F172A] flex items-center justify-center p-6 relative overflow-hidden">
            <div className="absolute top-[-10%] right-[-10%] w-[500px] h-[500px] bg-blue-600/10 blur-[120px] rounded-full -z-10" />
            <div className="absolute bottom-[-10%] left-[-10%] w-[500px] h-[500px] bg-purple-600/10 blur-[120px] rounded-full -z-10" />

            <div className="w-full max-w-4xl grid grid-cols-1 md:grid-cols-2 gap-12 items-center z-10">
                <div className="flex flex-col items-center md:items-start space-y-6 text-center md:text-left">
                    <img src={mascot} alt="Mascot" className="w-64 h-64 drop-shadow-[0_0_30px_rgba(59,130,246,0.2)]" />
                    <div className="space-y-2">
                        <h1 className="text-6xl font-black text-white tracking-tighter flex items-center gap-3">
                            Trellis <Sparkles className="text-blue-400" size={40} />
                        </h1>
                        <p className="text-slate-400 text-xl">Your peer-to-peer guardian.</p>
                    </div>
                </div>

                <div className="bg-white/[0.03] backdrop-blur-2xl border border-white/10 p-10 rounded-[3rem] shadow-2xl">
                    <h2 className="text-3xl font-bold text-white mb-6">{isLogin ? 'Welcome Back!' : 'Join Trellis'}</h2>
                    <form onSubmit={handleAction} className="space-y-4">
                        {!isLogin && (
                            <div className="relative">
                                <User className="absolute left-4 top-4 text-slate-500" size={20} />
                                <input type="text" placeholder="Full Name" required className="w-full bg-slate-900/50 border border-white/5 rounded-2xl py-4 pl-12 text-white outline-none" onChange={(e) => setFormData({ ...formData, username: e.target.value })} />
                            </div>
                        )}
                        <div className="relative">
                            <Mail className="absolute left-4 top-4 text-slate-500" size={20} />
                            <input type="email" placeholder="Email" required className="w-full bg-slate-900/50 border border-white/5 rounded-2xl py-4 pl-12 text-white outline-none" onChange={(e) => setFormData({ ...formData, email: e.target.value })} />
                        </div>
                        <div className="relative">
                            <Lock className="absolute left-4 top-4 text-slate-500" size={20} />
                            <input type="password" placeholder="Password" required className="w-full bg-slate-900/50 border border-white/5 rounded-2xl py-4 pl-12 text-white outline-none" onChange={(e) => setFormData({ ...formData, password: e.target.value })} />
                        </div>

                        <div className="w-full h-24 flex justify-center items-center">
                            <Lottie animationData={authAnimation} loop={true} className="h-32" />
                        </div>

                        <button type="submit" className="w-full bg-gradient-to-r from-blue-600 to-indigo-600 text-white font-bold py-4 rounded-2xl flex items-center justify-center gap-2">
                            {isLogin ? 'Sign In' : 'Create Account'} <ArrowRight size={20} />
                        </button>
                    </form>
                    <button onClick={() => setIsLogin(!isLogin)} className="w-full text-center text-slate-400 mt-4 text-sm hover:text-white transition-colors">
                        {isLogin ? "Need an account? Sign up" : "Already a member? Log in"}
                    </button>
                </div>
            </div>
        </div>
    );
};

export default Auth;
