import React from "react";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";
import trellisMascot1 from "../assets/trellis_mascot.png";

const Onboarding = () => {
    const navigate = useNavigate();

    return (
        <div className="flex flex-col min-h-screen w-full justify-between pt-8 pb-12 px-6 bg-[#1c1529] items-center relative overflow-hidden font-['Varela_Round',sans-serif]">

            {/* Background elements */}
            <div className="absolute top-1/4 left-1/4 w-96 h-96 bg-purple-600/5 rounded-full blur-[120px] pointer-events-none" />
            <div className="absolute bottom-1/4 right-1/4 w-96 h-96 bg-blue-600/5 rounded-full blur-[120px] pointer-events-none" />

            <div className="flex flex-col justify-center flex-1 grow items-center relative z-10">
                <div className="flex flex-col items-center justify-center relative">

                    {/* Character Container */}
                    <motion.div
                        initial={{ y: 20, opacity: 0 }}
                        animate={{ y: 0, opacity: 1 }}
                        transition={{ duration: 0.8 }}
                        className="relative w-64 h-80 flex flex-col items-center justify-end"
                    >
                        {/* Mascot */}
                        <img
                            className="w-full h-auto object-contain drop-shadow-2xl"
                            alt="Trellis mascot"
                            src={trellisMascot1}
                        />

                        {/* Speech Bubble */}
                        <motion.div
                            initial={{ scale: 0, opacity: 0 }}
                            animate={{ scale: 1, opacity: 1 }}
                            transition={{ delay: 0.5, type: "spring", stiffness: 200 }}
                            className="absolute -top-12 left-1/2 -translate-x-1/2 whitespace-nowrap"
                        >
                            <div className="relative bg-white px-6 py-3 rounded-2xl border-2 border-[#cccccc] shadow-lg">
                                <span className="text-[#000000de] text-xl font-medium text-center block">
                                    Hi there, I'm Yip!
                                </span>
                                {/* Arrow */}
                                <div className="absolute top-full left-[calc(50.00%_-_8px)] w-4 h-4 bg-white border-r-2 border-b-2 border-[#cccccc] rotate-45 -translate-y-[9px]" />
                            </div>
                        </motion.div>
                    </motion.div>
                </div>
            </div>

            {/* Bottom Button Area */}
            <div className="w-full max-w-4xl flex items-center justify-end relative z-10 px-10">
                <motion.button
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    onClick={() => navigate('/register-step2')} // Placeholder for next step
                    className="box-border inline-flex min-w-[160px] justify-center px-10 py-4 bg-[#575290] rounded-2xl shadow-[0px_5px_0px_#4c2b66] items-center relative transition-all hover:bg-[#635cb0] active:shadow-none active:translate-y-[5px]"
                >
                    <span className="text-[#ffffffde] text-xl font-bold text-center uppercase tracking-widest">
                        CONTINUE
                    </span>
                </motion.button>
            </div>
        </div>
    );
};

export default Onboarding;
