import React from 'react';
import { motion } from 'framer-motion';
import {
    Layout,
    Zap,
    Trophy,
    Target,
    BookOpen,
    Settings,
    ChevronRight,
    ChevronLeft,
    Lock,
    Eye,
    Star,
    Flame,
    Crown,
    Map
} from 'lucide-react';
import YipHead from '../assets/Yip_head.png';
import mascot from '../assets/trellis_mascot.png';
import { useNavigate } from 'react-router-dom';
import { courseData } from '../data/courses';

const Dashboard = () => {
    const navigate = useNavigate();
    const currentCourseId = localStorage.getItem('selectedCourse') || 'html';
    const currentSectionId = parseInt(localStorage.getItem('selectedSectionId')) || 1;

    const currentCourse = courseData[currentCourseId] || courseData.html;
    const currentSection = currentCourse.sections.find(s => s.id === currentSectionId) || currentCourse.sections[0];

    const dailyGoals = [
        { id: 1, title: 'Earn 90 XP', current: 0, total: 90, icon: <Zap className="w-5 h-5 text-orange-400" /> },
        { id: 2, title: 'Complete 3 exercises', current: 0, total: 3, icon: <BookOpen className="w-5 h-5 text-blue-400" /> },
        { id: 3, title: 'Solve 2 challenges on the first try', current: 0, total: 2, icon: <Target className="w-5 h-5 text-red-400" /> }
    ];

    const learningPath = currentSection.lessons;

    return (
        <div className="flex min-h-screen bg-[#111827] text-white font-['Inter',sans-serif]">

            {/* LEFT SIDEBAR: Navigation */}
            <aside className="w-16 md:w-24 fixed left-0 top-0 bottom-0 bg-[#1f2937] border-r border-white/5 flex flex-col items-center py-6 z-50">
                <div className="mb-8 px-2 transition-transform hover:scale-110 cursor-pointer" onClick={() => navigate('/dashboard')}>
                    <img src={YipHead} alt="Yip" className="w-full h-auto drop-shadow-[0_4px_15px_rgba(74,144,226,0.5)]" />
                </div>

                <nav className="flex flex-col gap-6">
                    <NavItem icon={<Map className="w-6 h-6" />} active />
                    <NavItem icon={<Zap className="w-6 h-6" />} />
                    <NavItem icon={<Trophy className="w-6 h-6" />} />
                    <NavItem icon={<BookOpen className="w-6 h-6" />} />
                    <NavItem icon={<Target className="w-6 h-6" />} />
                </nav>

                <div className="mt-auto">
                    <NavItem icon={<Settings className="w-7 h-7" />} />
                </div>
            </aside>

            {/* MAIN CONTENT AREA */}
            <main className="flex-1 ml-16 md:ml-24 mr-0 lg:mr-[300px] p-4 lg:p-6 flex flex-col items-center">

                {/* Learning Path Header */}
                <div className="w-full max-w-2xl bg-[#1e293b] rounded-xl p-4 mb-8 relative overflow-hidden shadow-2xl border border-white/5">
                    <button
                        onClick={() => navigate('/sections')}
                        className="flex items-center gap-1.5 text-slate-400 text-[10px] font-bold uppercase tracking-widest mb-1 hover:text-white transition-colors group"
                    >
                        <ChevronLeft className="w-3 h-3 group-hover:-translate-x-0.5 transition-transform" />
                        <span>Section {currentSection.id}, {currentSection.title}</span>
                    </button>
                    <h1 className="text-2xl font-black italic tracking-tight">{currentCourse.title}</h1>
                    <div className="absolute top-0 right-0 w-24 h-24 bg-blue-500/10 blur-[50px] rounded-full -mr-12 -mt-12" />
                </div>

                <div className="flex flex-col items-center gap-4 relative pb-10">
                    {/* Connection Line */}
                    <div className="absolute top-0 bottom-0 left-1/2 -translate-x-1/2 w-1 bg-slate-800" />

                    {learningPath.map((node, index) => (
                        <PathNode key={node.id} node={node} index={index} />
                    ))}
                </div>
            </main>

            {/* RIGHT SIDEBAR: Stats & Goals */}
            <aside className="hidden lg:flex w-[300px] fixed right-0 top-0 bottom-0 bg-[#111827] border-l border-white/5 flex-col p-6 gap-6 invisible-scrollbar overflow-y-auto">

                {/* Stats Bar */}
                <div className="flex justify-between items-center bg-[#1f2937] p-3 rounded-xl border border-white/5 shadow-lg relative">
                    <StatItem value="5">
                        <JourneysPopover />
                    </StatItem>
                    <StatItem value="0">
                        <StreakPopover />
                    </StatItem>
                </div>

                {/* Leaderboard Section */}
                <div className="bg-[#1f2937] rounded-2xl p-5 border border-white/5 shadow-xl">
                    <div className="flex justify-between items-center mb-4">
                        <h3 className="text-lg font-bold italic uppercase tracking-tighter">Leaderboard</h3>
                        <button className="text-blue-400 text-xs font-bold hover:underline">View</button>
                    </div>
                    <div className="flex flex-col items-center py-5 bg-[#2d3748]/50 rounded-xl border border-white/5">
                        <div className="w-16 h-16 bg-slate-700/50 rounded-xl flex items-center justify-center mb-3 relative overflow-hidden">
                            <Lock className="w-6 h-6 text-slate-500" />
                            <div className="absolute inset-0 bg-gradient-to-t from-black/20 to-transparent" />
                        </div>
                        <p className="text-slate-400 text-center text-xs font-medium px-4 leading-relaxed">
                            Reach <span className="text-white font-bold">100 XP</span> to unlock<br />leaderboards!
                        </p>
                    </div>
                </div>

                {/* Daily Goals Section */}
                <div className="bg-[#1f2937] rounded-2xl p-5 border border-white/5 shadow-xl">
                    <div className="flex justify-between items-center mb-4">
                        <h3 className="text-lg font-bold italic uppercase tracking-tighter">Daily Goals</h3>
                        <button className="text-blue-400 text-xs font-bold hover:underline">View</button>
                    </div>

                    <div className="space-y-4">
                        {dailyGoals.map(goal => (
                            <div key={goal.id} className="space-y-1.5">
                                <div className="flex justify-between items-center">
                                    <span className="text-xs font-bold text-slate-300">{goal.title}</span>
                                    <span className="text-[10px] font-black text-slate-500">{goal.current}/{goal.total}</span>
                                </div>
                                <div className="h-2 w-full bg-slate-800 rounded-full overflow-hidden relative">
                                    <div
                                        className={`h-full bg-gradient-to-r from-blue-500 to-indigo-500 rounded-full`}
                                        style={{ width: `${(goal.current / goal.total) * 100}%` }}
                                    />
                                </div>
                            </div>
                        ))}
                    </div>
                </div>

                {/* Branding footer */}
                <div className="mt-auto pt-6 text-center">
                    <p className="text-[10px] text-slate-600 font-bold uppercase tracking-[0.2em] flex items-center justify-center gap-4">
                        <span>About</span>
                        <span>Certifications</span>
                        <span>Affiliate</span>
                    </p>
                </div>
            </aside>
        </div>
    );
};

const NavItem = ({ icon, active = false, onClick }) => (
    <button
        onClick={onClick}
        className={`w-10 h-10 flex items-center justify-center rounded-xl transition-all ${active ? 'bg-blue-600 text-white shadow-lg shadow-blue-600/30' : 'text-slate-400 hover:bg-white/5 hover:text-white'
            }`}>
        {icon}
    </button>
);

const StatItem = ({ value, children }) => (
    <div className="flex flex-col items-center gap-0.5 p-2 bg-slate-800/40 rounded-xl border border-white/5 group relative cursor-help flex-1 min-w-[80px] transition-all hover:bg-slate-800/60">
        <div className="w-8 h-8 flex items-center justify-center">
            {value === "5" ? (
                <div className="w-7 h-7 bg-orange-600 rounded-md flex items-center justify-center font-black text-sm italic shadow-lg shadow-orange-900/20 text-white">5</div>
            ) : (
                <Flame className="w-6 h-6 text-orange-500" />
            )}
        </div>
        <span className="text-[10px] font-black text-slate-300">{value}</span>

        {/* Popover/Tooltip Content (Covers Icon) */}
        <div className="absolute top-0 left-1/2 -translate-x-[35%] opacity-0 group-hover:opacity-100 transition-opacity z-50 pointer-events-none group-hover:pointer-events-auto">
            {children}
        </div>
    </div>
);

const JourneysPopover = () => {
    const navigate = useNavigate();
    const activeCourseId = localStorage.getItem('selectedCourse') || 'html';
    const activeCourse = courseData[activeCourseId] || courseData.html;

    return (
        <div className="w-[280px] bg-[#1f2937] rounded-3xl overflow-hidden shadow-2xl border border-white/10 p-2 transform origin-top transition-transform scale-95 group-hover:scale-100">
            <div className="p-4 border-b border-white/5">
                <h4 className="text-slate-400 font-bold text-xs uppercase tracking-widest opacity-70">My Journeys</h4>
            </div>

            <div className="p-2 space-y-1">
                <button className="w-full flex items-center gap-4 p-3 bg-white/5 rounded-2xl border border-white/10 hover:bg-white/10 transition-all text-left group/item">
                    <div className="w-12 h-12 bg-orange-600 rounded-xl flex items-center justify-center font-black text-xl italic shadow-lg shadow-orange-900/40 text-white">5</div>
                    <span className="font-black italic uppercase text-xl tracking-tight text-white group-hover/item:translate-x-1 transition-transform">
                        {activeCourseId.toUpperCase()}
                    </span>
                </button>

                <button
                    onClick={() => navigate('/journeys')}
                    className="w-full flex items-center gap-4 p-4 hover:bg-white/5 rounded-2xl transition-all text-left group/add text-slate-500 hover:text-white"
                >
                    <div className="w-10 h-10 border-2 border-dashed border-slate-700 rounded-xl flex items-center justify-center group-hover/add:border-blue-500/50 group-hover/add:bg-white/5">
                        <Map className="w-5 h-5" />
                    </div>
                    <span className="font-bold text-base tracking-tight italic uppercase opacity-60 group-hover/add:opacity-100">Add Journey</span>
                </button>
            </div>
        </div>
    );
};

const StreakPopover = () => {
    const navigate = useNavigate();
    return (
        <div className="w-[320px] bg-[#1f2937] rounded-[2.5rem] overflow-hidden shadow-2xl border border-white/10 transform origin-top transition-transform scale-95 group-hover:scale-100">
            {/* Header Section with Reference Gradient */}
            <div className="bg-[#854d0e] p-7 text-left relative overflow-hidden">
                <h4 className="text-3xl font-black italic uppercase tracking-tighter mb-1 relative z-10 text-white">0 day streak</h4>
                <p className="text-orange-100/60 text-[12px] font-bold leading-tight max-w-[180px] relative z-10">
                    Do a lesson today to start a new streak!
                </p>
                {/* Mascot flame silhouette */}
                <Flame className="absolute -right-4 -bottom-4 w-36 h-36 text-orange-900/30 rotate-12" />
                <div className="absolute right-8 top-1/2 -translate-y-1/2 w-20 h-20 bg-white/10 rounded-full blur-2xl" />
                <Flame className="absolute right-10 top-1/2 -translate-y-1/2 w-16 h-16 text-white opacity-40 drop-shadow-lg" />
            </div>

            {/* Content Section */}
            <div className="p-7 space-y-8 bg-[#1f2937]">
                {/* Week Progress */}
                <div className="flex justify-between items-center px-2">
                    {['S', 'M', 'T', 'W', 'T', 'F', 'S'].map((day, i) => (
                        <div key={i} className="flex flex-col items-center gap-3">
                            <span className="text-[11px] font-black text-slate-500 tracking-wider font-mono">{day}</span>
                            <div className={`w-9 h-9 rounded-full border-2 flex items-center justify-center transition-all ${i === 6 ? 'border-slate-700 bg-slate-800/30' : 'border-slate-800 bg-slate-800/50 opacity-40'}`}>
                                {i === 6 && <Star className="w-4 h-4 text-slate-700" />}
                            </div>
                        </div>
                    ))}
                </div>

                {/* Streak Club Box - High Fidelity */}
                <div className="bg-[#111827] rounded-[2rem] p-5 border border-white/5 flex gap-5 items-center shadow-inner">
                    <div className="w-14 h-14 rounded-2xl bg-[#1f2937] flex items-center justify-center shrink-0 shadow-lg border border-white/5">
                        <Lock className="w-7 h-7 text-slate-600" />
                    </div>
                    <div className="space-y-3 flex-1">
                        <div>
                            <h5 className="text-sm font-black text-white mb-0.5 uppercase tracking-tight italic">Streak Club</h5>
                            <p className="text-[10px] text-slate-500 font-bold leading-tight uppercase tracking-tighter opacity-60">
                                Reach 7 days to earn perks
                            </p>
                        </div>
                        {/* Progress Bar */}
                        <div className="h-2.5 w-full bg-[#1f2937] rounded-full relative overflow-hidden border border-white/5">
                            <div className="absolute right-0 top-0 bottom-0 w-8 bg-gradient-to-l from-orange-500/20 to-transparent" />
                            <Flame className="absolute right-2 top-1/2 -translate-y-1/2 w-2.5 h-2.5 text-slate-700" />
                        </div>
                    </div>
                </div>

                {/* View More Button */}
                <button
                    onClick={() => navigate('/streak')}
                    className="w-full py-4 bg-[#0891b2] hover:bg-[#0e7490] text-white font-black italic uppercase text-base rounded-2xl transition-all shadow-xl shadow-cyan-900/30 active:scale-95"
                >
                    VIEW MORE
                </button>
            </div>
        </div>
    );
};

const PathNode = ({ node, index }) => {
    const [isHovered, setIsHovered] = React.useState(false);

    return (
        <motion.div
            initial={{ scale: 0.8, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            transition={{ delay: index * 0.1 }}
            className="relative z-10"
            onMouseEnter={() => setIsHovered(true)}
            onMouseLeave={() => setIsHovered(false)}
        >
            <div className={`w-20 h-20 md:w-24 md:h-24 rounded-[1.5rem] flex items-center justify-center border-4 transition-all transform hover:scale-105 cursor-pointer ${node.status === 'current'
                ? 'bg-[#1e4e6e] border-blue-400 shadow-[0_0_30px_rgba(96,165,250,0.2)]'
                : 'bg-[#1f2937] border-slate-800'
                }`}>
                {node.status === 'current' && (
                    <div className="absolute -top-10 bg-[#2d3748] border border-white/10 px-4 py-1.5 rounded-lg shadow-2xl z-20">
                        <span className="text-white font-black italic uppercase text-sm tracking-tight">START</span>
                        <div className="absolute -bottom-1 left-1/2 -translate-x-1/2 w-2 h-2 bg-[#2d3748] border-r border-b border-white/10 rotate-45" />
                    </div>
                )}

                {node.status === 'current' ? (
                    <div className="w-12 h-12 md:w-14 md:h-14 bg-blue-500 rounded-full flex items-center justify-center shadow-inner">
                        <Zap className="w-6 h-6 md:w-7 h-7 text-white fill-current" />
                    </div>
                ) : (
                    <Lock className="w-6 h-6 md:w-7 h-7 text-slate-700" />
                )}
            </div>

            {/* Lesson Name Tooltip on Hover (Now on TOP) */}
            {isHovered && (
                <motion.div
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="absolute bottom-[calc(100%+16px)] left-1/2 -translate-x-1/2 bg-[#2d3748] border border-white/5 px-6 py-4 rounded-2xl shadow-2xl z-50 text-center min-w-[220px]"
                >
                    <h4 className="text-white font-black italic uppercase text-sm mb-1">{node.title}</h4>
                    <p className="text-slate-400 text-[10px] font-bold uppercase tracking-widest leading-relaxed">
                        {node.status === 'locked' ? 'Complete the lesson above to unlock this!' : 'Begin your journey!'}
                    </p>
                    {node.status === 'locked' && (
                        <div className="mt-3 w-max mx-auto px-4 py-1.5 bg-slate-700/50 rounded-xl text-[10px] font-black text-slate-500 uppercase">
                            LOCKED
                        </div>
                    )}
                    <div className="absolute -bottom-1.5 left-1/2 -translate-x-1/2 w-3 h-3 bg-[#2d3748] border-b border-r border-white/5 rotate-45" />
                </motion.div>
            )}
        </motion.div>
    );
};

export default Dashboard;
