import React from 'react';
import { useLocation } from 'react-router-dom';
import mascot from '../assets/trellis_mascot.png';

const Dashboard = () => {
    const { state } = useLocation();
    const user = state?.user || { username: "User", progress: 0 };

    return (
        <div className="min-h-screen bg-[#0F172A] p-8 flex flex-col items-center">
            <div className="glass-container rounded-[2.5rem] p-10 w-full max-w-4xl flex flex-col md:flex-row items-center gap-8">
                <img src={mascot} alt="Mascot" className="w-32 animate-float" />
                <div className="flex-grow space-y-4">
                    <h2 className="text-4xl font-bold text-white italic">Welcome back, {user.username}!</h2>
                    <div className="w-full bg-slate-800 rounded-full h-2">
                        <div className="bg-blue-500 h-full rounded-full" style={{ width: `${user.progress}%` }}></div>
                    </div>
                    <p className="text-blue-400 font-bold text-sm">CURRENT PROGRESS: {user.progress}%</p>
                </div>
            </div>

            {/* Action Buttons Placeholders */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mt-12 w-full max-w-4xl">
                <button className="glass-container p-8 rounded-2xl font-black text-xl hover:bg-white/10">MATCHMAKING AI</button>
                <button className="glass-container p-8 rounded-2xl font-black text-xl hover:bg-white/10">KTU RESOURCES</button>
            </div>
        </div>
    );
};

export default Dashboard;
