import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Eye, EyeOff, Mail, Lock } from 'lucide-react';
import logo from './assets/Logo.jpeg';

const Login = () => {
    const navigate = useNavigate();
    const [show, setShow] = useState(false);

    const s = {
        wrapper: { height: '100vh', width: '100vw', display: 'flex', justifyContent: 'center', alignItems: 'center' },
        card: {
            background: 'rgba(255, 255, 255, 0.03)', backdropFilter: 'blur(25px)',
            padding: '50px 45px', borderRadius: '40px', width: '440px',
            display: 'flex', flexDirection: 'column', alignItems: 'center',
            border: '1px solid rgba(255, 255, 255, 0.1)', boxShadow: '0 25px 50px rgba(0, 0, 0, 0.6)'
        },
        inputBox: { width: '100%', position: 'relative', marginBottom: '18px' },
        input: {
            width: '100%', padding: '16px 16px 16px 50px', borderRadius: '16px',
            border: '1px solid rgba(255,255,255,0.1)', background: 'rgba(255,255,255,0.05)',
            color: 'white', outline: 'none'
        }
    };

    return (
        <div style={s.wrapper}>
            <div style={s.card}>
                <img src={logo} alt="Trellis" style={{width: '130px', mixBlendMode: 'screen', marginBottom: '20px'}} />
                <h2 style={{color: 'white', fontSize: '28px', fontWeight: '800', marginBottom: '30px'}}>Login</h2>
                <form style={{width: '100%'}}>
                    <div style={s.inputBox}>
                        <Mail size={18} style={{position: 'absolute', left: '18px', top: '50%', transform: 'translateY(-50%)', color: '#9133ff'}} />
                        <input style={s.input} type="email" placeholder="Email Address" />
                    </div>
                    <div style={s.inputBox}>
                        <Lock size={18} style={{position: 'absolute', left: '18px', top: '50%', transform: 'translateY(-50%)', color: '#9133ff'}} />
                        <input style={s.input} type={show ? "text" : "password"} placeholder="Password" />
                        <button type="button" onClick={() => setShow(!show)} style={{position: 'absolute', right: '15px', top: '50%', transform: 'translateY(-50%)', background: 'none', border: 'none', color: '#666', cursor: 'pointer'}}>
                            {show ? <EyeOff size={18} /> : <Eye size={18} />}
                        </button>
                    </div>
                    <button style={{width: '100%', padding: '18px', background: 'linear-gradient(135deg, #9133ff 0%, #6a00f4 100%)', color: 'white', border: 'none', borderRadius: '16px', fontWeight: '800', cursor: 'pointer', marginTop: '10px'}}>LOGIN</button>
                </form>
                <p style={{color: '#999', marginTop: '30px', fontSize: '14px'}}>New to Trellis? <span onClick={() => navigate('/register')} style={{color: '#9133ff', fontWeight: '700', cursor: 'pointer'}}>Sign Up</span></p>
            </div>
        </div>
    );
};

export default Login;
