    return (
        <div className="w-[320px] bg-[#1f2937] rounded-[2.5rem] overflow-hidden shadow-2xl border border-white/10 transform origin-top transition-transform scale-95 group-hover:scale-100">
            {/* Header Section with Reference Gradient */}
            <div className="bg-[#854d0e] p-7 text-left relative overflow-hidden">
                <h4 className="text-3xl font-black italic uppercase tracking-tighter mb-1 relative z-10 text-white">{streak} day streak</h4>
                <p className="text-orange-100/60 text-[12px] font-bold leading-tight max-w-[180px] relative z-10">
                    {streak > 0 ? "You're on fire! Keep it up!" : "Do a lesson today to start a new streak!"}
                </p>
                {/* Mascot flame silhouette */}
