                <div className="flex justify-between items-center px-2">
                    {['S', 'M', 'T', 'W', 'T', 'F', 'S'].map((day, i) => (
                        <div key={i} className="flex flex-col items-center gap-3">
                            <span className="text-[11px] font-black text-slate-500 tracking-wider font-mono">{day}</span>
                            <div className={`w-9 h-9 rounded-full border-2 flex items-center justify-center transition-all ${i === 6 ? 'border-slate-700 bg-slate-800/30' : 'border-slate-800 bg-slate-800/50 opacity-40'}`}>
                                {i === 6 && <Star className="w-4 h-4 text-slate-700" />}
                            </div>
                        </div>
                    ))}
