                {/* Streak Club Box - High Fidelity */}
                <div className="bg-[#111827] rounded-[2rem] p-5 border border-white/5 flex gap-5 items-center shadow-inner">
                    <div className="w-14 h-14 rounded-2xl bg-[#1f2937] flex items-center justify-center shrink-0 shadow-lg border border-white/5">
                        <Lock className="w-7 h-7 text-slate-600" />
                    </div>
                    <div className="space-y-3 flex-1">
                        <div>
                            <h5 className="text-sm font-black text-white mb-0.5 uppercase tracking-tight italic">Streak Club</h5>
                            <p className="text-[10px] text-slate-500 font-bold leading-tight uppercase tracking-tighter opacity-60">
                                Reach 7 days to earn perks
                            </p>
                        </div>
                        {/* Progress Bar */}
                        <div className="h-2.5 w-full bg-[#1f2937] rounded-full relative overflow-hidden border border-white/5">
                            <div className="absolute right-0 top-0 bottom-0 w-8 bg-gradient-to-l from-orange-500/20 to-transparent" />
                            <Flame className="absolute right-2 top-1/2 -translate-y-1/2 w-2.5 h-2.5 text-slate-700" />
                        </div>
                    </div>
                </div>

                {/* View More Button */}
                <button
                    onClick={() => navigate('/streak')}
                    className="w-full py-4 bg-[#0891b2] hover:bg-[#0e7490] text-white font-black italic uppercase text-base rounded-2xl transition-all shadow-xl shadow-cyan-900/30 active:scale-95"
                >
                    VIEW MORE
                </button>
            </div>
        </div>
    );
};

const PathNode = ({ node, index, onClick }) => {
    const [isHovered, setIsHovered] = React.useState(false);

    return (
        <motion.div
            initial={{ scale: 0.8, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            transition={{ delay: index * 0.1 }}
            className="relative z-10"
            onMouseEnter={() => setIsHovered(true)}
            onMouseLeave={() => setIsHovered(false)}
            onClick={node.status !== 'locked' ? onClick : undefined}
        >
            <div className={`w-20 h-20 md:w-24 md:h-24 rounded-[1.5rem] flex items-center justify-center border-4 transition-all transform hover:scale-105 cursor-pointer ${node.status === 'current'
                ? 'bg-[#1e4e6e] border-blue-400 shadow-[0_0_30px_rgba(96,165,250,0.2)]'
                : 'bg-[#1f2937] border-slate-800'
                }`}>
                {node.status === 'current' && (
                    <div className="absolute -top-10 bg-[#2d3748] border border-white/10 px-4 py-1.5 rounded-lg shadow-2xl z-20">
                        <span className="text-white font-black italic uppercase text-sm tracking-tight">START</span>
                        <div className="absolute -bottom-1 left-1/2 -translate-x-1/2 w-2 h-2 bg-[#2d3748] border-r border-b border-white/10 rotate-45" />
                    </div>
                )}

                {node.status === 'current' ? (
                    <div className="w-12 h-12 md:w-14 md:h-14 bg-blue-500 rounded-full flex items-center justify-center shadow-inner">
                        <Play className="w-6 h-6 md:w-7 h-7 text-white fill-current" />
                    </div>
                ) : (
                    <Lock className="w-6 h-6 md:w-7 h-7 text-slate-700" />
                )}
            </div>

            {/* Lesson Name Tooltip on Hover (Now on the RIGHT) */}
            {isHovered && (
                <motion.div
                    initial={{ opacity: 0, x: -10 }}
                    animate={{ opacity: 1, x: 0 }}
                    className="absolute left-[calc(100%+20px)] top-1/2 -translate-y-1/2 bg-[#2d3748] border border-white/5 px-4 py-3 rounded-xl shadow-2xl z-50 text-left min-w-[180px]"
                >
                    <h4 className="text-white font-black italic uppercase text-xs mb-1">{node.title}</h4>
                    <p className="text-slate-400 text-[9px] font-bold uppercase tracking-widest leading-tight">
                        {node.status === 'locked' ? 'Complete the lesson above to unlock this!' : 'Begin your journey!'}
                    </p>
                    {node.status === 'locked' && (
                        <div className="mt-2 w-max px-3 py-1 bg-slate-700/50 rounded-lg text-[9px] font-black text-slate-500 uppercase tracking-tighter">
                            LOCKED
                        </div>
                    )}
                    {/* Arrow pointing Left */}
                    <div className="absolute top-1/2 -left-1 -translate-y-1/2 w-2.5 h-2.5 bg-[#2d3748] border-l border-b border-white/5 rotate-45" />
                </motion.div>
            )}
        </motion.div>
    );
};

export default Dashboard;
