import React from 'react';
import { useLocation } from 'react-router-dom';
import mascot from '../assets/trellis_mascot.png';

const Dashboard = () => {
    const { state } = useLocation();
    const user = state?.user || { username: "Scholar", progress: 0 };

    return (
        <div className="flex min-h-screen flex-col items-center bg-[#0F172A] p-10 space-y-10 text-white">
            {/* Header card */}
            <div className="glass-card flex w-full max-w-4xl items-center gap-10 p-10">
                <img src={mascot} alt="Mascot" className="w-24" />
                <div className="flex-grow space-y-4">
                    <h2 className="text-4xl font-black italic">Welcome back, {user.username}!</h2>
                    <div className="h-3 w-full rounded-full bg-slate-800">
                        <div className="h-full rounded-full bg-blue-500" style={{ width: `${user.progress}%` }}></div>
                    </div>
                    <p className="text-blue-400 text-sm font-bold uppercase tracking-widest">Progress: {user.progress}%</p>
                </div>
            </div>

            {/* Grid */}
            <div className="grid w-full max-w-4xl grid-cols-1 md:grid-cols-2 gap-8">
                <button className="glass-card p-12 text-2xl font-black hover:bg-white/10 transition-all uppercase">Matchmaking AI</button>
                <button className="glass-card p-12 text-2xl font-black hover:bg-white/10 transition-all uppercase">Resources</button>
            </div>
        </div>
    );
};

export default Dashboard;
