import React, { useState, useRef } from 'react';
import Lottie from 'lottie-react';
import { Eye, EyeOff } from 'lucide-react'; // Professional icons
import animationData from './assets/login-animation.json';
import logo from './assets/Logo.jpeg';
import './Login.css';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [showPassword, setShowPassword] = useState(false);
    const lottieRef = useRef(null);

    const handleInputFocus = (inputType) => {
        if (lottieRef.current) {
            if (inputType === 'password') {
                lottieRef.current.playSegments([30, 60], true);
            } else {
                lottieRef.current.playSegments([0, 30], true);
            }
        }
    };

    return (
        <div className="space-background">
            <div className="stars"></div>
            <div className="login-card">
                <img src={logo} alt="Trellis" className="brand-logo-white" />
                
                <div className="animation-container-large">
                    <Lottie 
                        lottieRef={lottieRef}
                        animationData={animationData} 
                        loop={true} 
                    />
                </div>

                <form className="login-form">
                    <h2 className="page-title">Login</h2>
                    
                    <div className="input-box">
                        <input 
                            type="email" 
                            placeholder="Email Address" 
                            onFocus={() => handleInputFocus('email')}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                        />
                    </div>

                    <div className="input-box password-box">
                        <input 
                            type={showPassword ? "text" : "password"} 
                            placeholder="Password" 
                            onFocus={() => handleInputFocus('password')}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                        <button 
                            type="button" 
                            className="eye-icon" 
                            onClick={() => setShowPassword(!showPassword)}
                        >
                            {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
                        </button>
                    </div>

                    <button type="submit" className="login-btn">LOGIN</button>
                    
                    <div className="auth-footer">
                        <span>New to Trellis?</span>
                        <button type="button" className="signup-link">Sign Up</button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default Login;
