import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Lottie from 'lottie-react';
import { Mail, Lock, LogIn, ChevronRight, UserPlus } from 'lucide-react';

import logo from '../assets/Logo.jpeg';
import mascot from '../assets/trellis_mascot.png';
import loginAnim from '../assets/login-animation.json';

const Login = () => {
    const [formData, setFormData] = useState({ email: '', password: '' });
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.post('http://localhost:5000/api/auth/login', formData);
            if (res.data.token) {
                localStorage.setItem('token', res.data.token);
                navigate('/dashboard', { state: { user: res.data.user } });
            }
        } catch (err) {
            alert(err.response?.data?.msg || "Login failed");
        }
    };

    return (
        <div className="min-h-screen w-full bg-[#0F172A] relative overflow-hidden flex flex-col items-center justify-center p-6 px-10">

            {/* TOP LEFT: Logo */}
            <div className="absolute top-8 left-10 flex items-center gap-4">
                <img src={logo} alt="Trellis Logo" className="w-12 h-12 rounded-xl object-contain shadow-lg" />
                <h1 className="text-white text-3xl font-serif tracking-tight">Trellis</h1>
            </div>

            <div className="w-full max-w-7xl flex flex-col md:flex-row items-center justify-between gap-12 lg:gap-24">

                {/* LEFT: Animation (The "Orange Box" area) */}
                <motion.div
                    initial={{ x: -100, opacity: 0 }}
                    animate={{ x: 0, opacity: 1 }}
                    transition={{ duration: 0.8, ease: "easeOut" }}
                    className="w-full md:w-1/2 flex justify-center"
                >
                    <div className="w-full max-w-2xl bg-slate-900/40 rounded-[40px] p-6 border border-slate-800/50 backdrop-blur-sm shadow-2xl">
                        <Lottie animationData={loginAnim} loop={true} className="w-full h-auto" />
                    </div>
                </motion.div>

                {/* RIGHT: Mascot + Form */}
                <motion.div
                    initial={{ x: 100, opacity: 0 }}
                    animate={{ x: 0, opacity: 1 }}
                    transition={{ duration: 0.8, ease: "easeOut", delay: 0.2 }}
                    className="w-full md:w-[450px] flex flex-col items-center pt-10"
                >
                    {/* Mascot */}
                    <div className="mb-8 hover:scale-105 transition-transform duration-300">
                        <img src={mascot} alt="Trellis Mascot" className="w-32 h-auto drop-shadow-[0_0_15px_rgba(236,72,153,0.3)]" />
                    </div>

                    <form onSubmit={handleLogin} className="w-full space-y-6">
                        {/* Email Input */}
                        <div className="relative group">
                            <input
                                type="email"
                                placeholder="Enter Email"
                                className="w-full px-8 py-5 rounded-[30px] bg-[#1e4e6e]/40 border border-[#3b82f6]/30 text-white placeholder-slate-400 focus:outline-none focus:ring-2 focus:ring-[#3b82f6]/50 transition-all text-lg shadow-inner"
                                value={formData.email}
                                onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                                required
                            />
                        </div>

                        {/* Password Input */}
                        <div className="relative group">
                            <input
                                type="password"
                                placeholder="Enter Password"
                                className="w-full px-8 py-5 rounded-[30px] bg-[#1e4e6e]/40 border border-[#3b82f6]/30 text-white placeholder-slate-400 focus:outline-none focus:ring-2 focus:ring-[#3b82f6]/50 transition-all text-lg shadow-inner"
                                value={formData.password}
                                onChange={(e) => setFormData({ ...formData, password: e.target.value })}
                                required
                            />
                        </div>

                        {/* Sign In Button */}
                        <motion.button
                            whileHover={{ scale: 1.02 }}
                            whileTap={{ scale: 0.98 }}
                            type="submit"
                            className="w-full py-5 rounded-[30px] bg-[#3b82f6] text-white font-black text-xl uppercase tracking-[0.2em] shadow-[0_10px_30px_rgba(59,130,246,0.5)] transition-all hover:bg-[#2563eb] hover:shadow-[0_15px_40px_rgba(59,130,246,0.7)] flex items-center justify-center gap-3"
                        >
                            Sign In
                        </motion.button>
                    </form>

                    {/* Register Button */}
                    <div className="mt-8 w-full flex flex-col items-center gap-4">
                        <p className="text-slate-500 text-sm">New to Trellis?</p>
                        <button
                            onClick={() => navigate('/register')}
                            className="text-indigo-400 text-lg font-bold hover:text-white transition-colors flex items-center gap-2 group underline decoration-indigo-500/50 underline-offset-8"
                        >
                            <UserPlus className="w-5 h-5 group-hover:rotate-12 transition-transform" />
                            Join the Community
                        </button>
                    </div>
                </motion.div>
            </div>

            {/* Decorative background blurs */}
            <div className="absolute -top-40 -left-40 w-96 h-96 bg-indigo-600/10 rounded-full blur-[120px] pointer-events-none" />
            <div className="absolute -bottom-40 -right-40 w-96 h-96 bg-blue-600/10 rounded-full blur-[120px] pointer-events-none" />
        </div>
    );
};

export default Login;
