import React, { useState, useRef } from 'react';
import Lottie from 'lottie-react';
import animationData from './assets/login-animation.json';
import logo from './assets/Logo.jpeg';
import './Login.css';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const lottieRef = useRef(null);

    const handleInputFocus = (inputType) => {
        if (lottieRef.current) {
            if (inputType === 'password') {
                lottieRef.current.playSegments([40, 70], true); // Frame range for "hiding eyes"
            } else {
                lottieRef.current.playSegments([10, 30], true); // Frame range for "looking down"
            }
        }
    };

    const handleBlur = () => {
        if (lottieRef.current) {
            lottieRef.current.playSegments([0, 10], true); // Return to idle
        }
    };

    return (
        <div className="login-container">
            <header className="login-header">
                <img src={logo} alt="Trellis" className="brand-logo" />
            </header>
            
            <div className="login-card">
                <div className="visual-section">
                    <Lottie 
                        lottieRef={lottieRef}
                        animationData={animationData} 
                        loop={true} 
                        style={{ height: 200 }}
                    />
                </div>
                
                <div className="form-section">
                    <h2>Welcome Back</h2>
                    <p>Enter your details to access your Trellis dashboard.</p>
                    
                    <form className="auth-form">
                        <div className="input-group">
                            <label>Email Address</label>
                            <input 
                                type="email" 
                                onFocus={() => handleInputFocus('email')}
                                onBlur={handleBlur}
                                onChange={(e) => setEmail(e.target.value)}
                                placeholder="name@company.com"
                            />
                        </div>
                        
                        <div className="input-group">
                            <label>Password</label>
                            <input 
                                type="password" 
                                onFocus={() => handleInputFocus('password')}
                                onBlur={handleBlur}
                                onChange={(e) => setPassword(e.target.value)}
                                placeholder="••••••••"
                            />
                        </div>
                        
                        <button type="submit" className="submit-btn">Sign In</button>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default Login;
