import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import trellisMascot from "../assets/trellis_mascot.png";

const Home = () => {
    const navigate = useNavigate();
    const [isGetStartedHovered, setIsGetStartedHovered] = useState(false);
    const [isSignInHovered, setIsSignInHovered] = useState(false);

    const handleGetStartedClick = () => {
        navigate("/register");
    };

    const handleSignInClick = () => {
        navigate("/login");
    };

    return (
        <main className="relative min-h-screen w-full bg-[#1b1529] flex flex-col items-center justify-center overflow-hidden py-20">

            {/* Main Content Container */}
            <div className="relative w-full max-w-4xl flex flex-col md:flex-row items-center justify-center gap-12 px-6 z-10">

                {/* Left Side: Mascot */}
                <div className="w-full md:w-1/2 flex justify-center">
                    <div className="relative group">
                        <div className="absolute inset-0 bg-indigo-500/10 blur-[100px] rounded-full group-hover:bg-indigo-500/20 transition-all duration-700" />
                        <img
                            className="w-full max-w-[450px] aspect-[1] object-contain relative z-10 drop-shadow-[0_20px_50px_rgba(0,0,0,0.5)] transition-transform duration-500 hover:scale-105"
                            alt="Trellis mascot"
                            src={trellisMascot}
                        />
                    </div>
                </div>

                {/* Right Side: Text & Buttons */}
                <div className="w-full md:w-1/2 flex flex-col items-center md:items-start text-center md:text-left">
                    <h1 className="mb-12 font-['Varela_Round',sans-serif] font-normal text-white/90 text-3xl md:text-4xl lg:text-5xl leading-tight tracking-tight">
                        The free, fun, and effective way
                        <br />
                        to learn to code!
                    </h1>

                    <div className="flex flex-col gap-6 w-full max-w-[350px]">
                        <button
                            className="w-full h-14 bg-[#784b99] rounded-2xl font-['Varela_Round',sans-serif] font-bold text-white text-lg tracking-widest uppercase shadow-[0px_6px_0px_#4c2b66] transition-all hover:translate-y-[2px] hover:shadow-[0px_4px_0px_#4c2b66] active:translate-y-[6px] active:shadow-none"
                            onClick={handleGetStartedClick}
                            onMouseEnter={() => setIsGetStartedHovered(true)}
                            onMouseLeave={() => setIsGetStartedHovered(false)}
                            aria-label="Get started with learning to code"
                            type="button"
                        >
                            GET STARTED
                        </button>

                        <button
                            className="w-full h-14 bg-[#3c3549] rounded-2xl border-2 border-[#b3b3b3] font-['Varela_Round',sans-serif] font-bold text-[#29abe2] text-lg tracking-widest uppercase shadow-[0px_5px_0px_#b3b3b3] transition-all hover:translate-y-[2px] hover:shadow-[0px_3px_0px_#b3b3b3] active:translate-y-[5px] active:shadow-none"
                            onClick={handleSignInClick}
                            onMouseEnter={() => setIsSignInHovered(true)}
                            onMouseLeave={() => setIsSignInHovered(false)}
                            aria-label="Sign in to existing account"
                            type="button"
                        >
                            I ALREADY HAVE AN ACCOUNT
                        </button>
                    </div>
                </div>
            </div>

            {/* Decorative background blurs */}
            <div className="absolute top-1/4 -left-20 w-96 h-96 bg-purple-600/10 rounded-full blur-[120px] pointer-events-none" />
            <div className="absolute bottom-1/4 -right-20 w-96 h-96 bg-blue-600/10 rounded-full blur-[120px] pointer-events-none" />
        </main>
    );
};

export default Home;
