import React from 'react';
import { motion } from 'framer-motion';
import {
    ChevronLeft,
    ChevronRight,
    Flame,
    Zap,
    BookOpen,
    Target,
    Lock,
    Star,
    Settings,
    Trophy,
    Map
} from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import YipHead from '../assets/Yip_head.png';

const Streak = () => {
    const navigate = useNavigate();

    const dailyGoals = [
        { id: 1, title: 'Earn 90 XP', current: 0, total: 90, icon: <Zap className="w-5 h-5 text-orange-400" /> },
        { id: 2, title: 'Complete 3 exercises', current: 0, total: 3, icon: <BookOpen className="w-5 h-5 text-blue-400" /> },
        { id: 3, title: 'Solve 2 challenges on the first try', current: 0, total: 2, icon: <Target className="w-5 h-5 text-red-400" /> }
    ];

    const daysInMonth = Array.from({ length: 28 }, (_, i) => i + 1);
    const dayLabels = ['S', 'M', 'T', 'W', 'T', 'F', 'S'];

    return (
        <div className="flex min-h-screen bg-[#111827] text-white font-['Inter',sans-serif]">

            {/* LEFT SIDEBAR: Navigation */}
            <aside className="w-20 md:w-32 fixed left-0 top-0 bottom-0 bg-[#1f2937] border-r border-white/5 flex flex-col items-center py-8 z-50">
                <div className="mb-12 px-1 transition-transform hover:scale-110 cursor-pointer" onClick={() => navigate('/dashboard')}>
                    <img src={YipHead} alt="Yip" className="w-full h-auto drop-shadow-[0_4px_20px_rgba(74,144,226,0.7)]" />
                </div>

                <nav className="flex flex-col gap-8">
                    <NavItem icon={<Map className="w-8 h-8" />} onClick={() => navigate('/dashboard')} />
                    <NavItem icon={<Zap className="w-8 h-8" />} />
                    <NavItem icon={<Trophy className="w-8 h-8" />} />
                    <NavItem icon={<BookOpen className="w-8 h-8" />} />
                    <NavItem icon={<Target className="w-8 h-8" />} />
                </nav>

                <div className="mt-auto">
                    <NavItem icon={<Settings className="w-9 h-9" />} />
                </div>
            </aside>

            {/* MAIN CONTENT AREA */}
            <main className="flex-1 ml-20 md:ml-32 mr-0 lg:mr-[350px] p-6 lg:p-10 flex flex-col items-center">
                <div className="w-full max-w-4xl space-y-10">

                    {/* Streak Hero Section */}
                    <div className="text-center space-y-4 pt-10 relative">
                        <motion.div
                            initial={{ scale: 0.5, opacity: 0 }}
                            animate={{ scale: 1, opacity: 1 }}
                            className="relative inline-block"
                        >
                            <Flame className="w-32 h-32 text-slate-700/50" />
                            <div className="absolute inset-0 flex items-center justify-center">
                                <span className="text-5xl font-black text-white italic drop-shadow-xl mt-4">0</span>
                            </div>
                        </motion.div>
                        <h1 className="text-5xl font-black italic uppercase tracking-tighter">0 day streak</h1>
                        <p className="text-slate-400 font-bold text-lg">Do a lesson today to start a new streak!</p>
                    </div>

                    {/* Calendar Section */}
                    <motion.div
                        initial={{ y: 20, opacity: 0 }}
                        animate={{ y: 0, opacity: 1 }}
                        className="bg-[#1f2937] rounded-[2.5rem] p-10 border border-white/5 shadow-2xl space-y-10"
                    >
                        {/* Month Header */}
                        <div className="flex justify-between items-center px-4">
                            <ChevronLeft className="w-8 h-8 text-slate-600 hover:text-white cursor-pointer transition-colors" />
                            <h2 className="text-2xl font-black italic uppercase tracking-tight text-slate-300">February 2026</h2>
                            <ChevronRight className="w-8 h-8 text-slate-600 hover:text-white cursor-pointer transition-colors" />
                        </div>

                        {/* Calendar Grid */}
                        <div className="grid grid-cols-7 gap-y-8 gap-x-4 text-center">
                            {dayLabels.map(day => (
                                <div key={day} className="text-slate-600 font-black text-sm uppercase tracking-widest mb-2">{day}</div>
                            ))}
                            {daysInMonth.map(day => (
                                <div key={day} className="relative flex items-center justify-center">
                                    <div className={`w-14 h-14 rounded-full flex items-center justify-center text-xl font-bold transition-all relative
                                        ${day === 16 ? 'border-4 border-orange-500 text-white shadow-[0_0_20px_rgba(249,115,22,0.3)]' : 'text-slate-700'}
                                    `}>
                                        {day}
                                        {day === 16 && (
                                            <div className="absolute -inset-1 border-2 border-orange-500/20 rounded-full animate-ping" />
                                        )}
                                    </div>
                                </div>
                            ))}
                        </div>
                    </motion.div>

                    {/* Double or Nothing Box */}
                    <motion.div
                        initial={{ y: 20, opacity: 0 }}
                        animate={{ y: 0, opacity: 1 }}
                        transition={{ delay: 0.1 }}
                        className="bg-[#1f2937] rounded-[2.5rem] p-10 border border-white/5 shadow-2xl flex items-center gap-10"
                    >
                        <div className="w-24 h-24 bg-slate-800/50 rounded-3xl flex items-center justify-center shrink-0 border border-white/5">
                            <div className="relative">
                                <Target className="w-12 h-12 text-slate-400 rotate-45" />
                                <Target className="w-12 h-12 text-slate-600 -rotate-45 absolute top-0 left-0" />
                            </div>
                        </div>
                        <div className="space-y-4 flex-1">
                            <h3 className="text-2xl font-black italic uppercase tracking-tight text-white">Double or Nothing</h3>
                            <p className="text-slate-400 font-bold text-lg leading-relaxed">
                                Double your 30 tokens wager by maintaining a 7 day streak.
                            </p>
                        </div>
                    </motion.div>

                </div>
            </main>

            {/* RIGHT SIDEBAR: Stats & Goals */}
            <aside className="hidden lg:flex w-[350px] fixed right-0 top-0 bottom-0 bg-[#111827] border-l border-white/5 flex-col p-8 gap-8 invisible-scrollbar overflow-y-auto">
                {/* Stats Bar */}
                <div className="flex justify-between items-center bg-[#1f2937] p-4 rounded-2xl border border-white/5 shadow-lg">
                    <StatItem value="5" />
                    <StatItem value="0" />
                </div>

                {/* Leaderboard Section */}
                <div className="bg-[#1f2937] rounded-3xl p-6 border border-white/5 shadow-xl">
                    <div className="flex justify-between items-center mb-6">
                        <h3 className="text-xl font-bold italic uppercase tracking-tighter">Leaderboard</h3>
                        <button className="text-blue-400 text-sm font-bold hover:underline">View</button>
                    </div>
                    <div className="flex flex-col items-center py-8 bg-[#2d3748]/50 rounded-2xl border border-white/5">
                        <div className="w-20 h-20 bg-slate-700/50 rounded-2xl flex items-center justify-center mb-4 relative overflow-hidden">
                            <Lock className="w-8 h-8 text-slate-500" />
                        </div>
                        <p className="text-slate-400 text-center text-sm font-medium px-6 leading-relaxed">
                            Reach <span className="text-white font-bold">100 XP</span> to unlock<br />leaderboards!
                        </p>
                    </div>
                </div>

                {/* Daily Goals Section */}
                <div className="bg-[#1f2937] rounded-3xl p-6 border border-white/5 shadow-xl">
                    <div className="flex justify-between items-center mb-6">
                        <h3 className="text-xl font-bold italic uppercase tracking-tighter">Daily Goals</h3>
                        <button className="text-blue-400 text-sm font-bold hover:underline">View</button>
                    </div>

                    <div className="space-y-6">
                        {dailyGoals.map(goal => (
                            <div key={goal.id} className="space-y-2">
                                <div className="flex justify-between items-center">
                                    <span className="text-sm font-bold text-slate-300">{goal.title}</span>
                                    <span className="text-xs font-black text-slate-500">{goal.current}/{goal.total}</span>
                                </div>
                                <div className="h-3 w-full bg-slate-800 rounded-full overflow-hidden relative">
                                    <div
                                        className="h-full bg-gradient-to-r from-blue-500 to-indigo-500 rounded-full"
                                        style={{ width: `${(goal.current / goal.total) * 100}%` }}
                                    />
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </aside>
        </div>
    );
};

const NavItem = ({ icon, active = false, onClick }) => (
    <button
        onClick={onClick}
        className={`w-12 h-12 flex items-center justify-center rounded-2xl transition-all ${active ? 'bg-blue-600 text-white shadow-lg shadow-blue-600/30' : 'text-slate-400 hover:bg-white/5 hover:text-white'
            }`}>
        {icon}
    </button>
);

const StatItem = ({ value }) => (
    <div className="flex flex-col items-center gap-1 p-3 bg-slate-800/40 rounded-2xl border border-white/5 flex-1 min-w-[100px] transition-all hover:bg-slate-800/60">
        <div className="w-10 h-10 flex items-center justify-center">
            {value === "5" ? (
                <div className="w-9 h-9 bg-orange-600 rounded-lg flex items-center justify-center font-black text-lg italic shadow-lg shadow-orange-900/20 text-white">5</div>
            ) : (
                <Flame className="w-7 h-7 text-orange-500" />
            )}
        </div>
        <span className="text-xs font-black text-slate-300">{value}</span>
    </div>
);

export default Streak;
