import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import {
    Terminal,
    FileCode,
    Codepen,
    Coffee,
    Cpu,
    Database,
    ChevronLeft,
    ChevronRight,
    Globe,
    Share2,
    Settings,
    HelpCircle,
    BarChart3,
    Layers,
    Zap,
    Shield,
    MessageSquare
} from "lucide-react";
import trellisMascot from "../assets/trellis_mascot.png";

const languageItems = [
    {
        id: "python",
        name: "Python",
        icon: <Terminal className="w-full h-full text-blue-400" />,
        hasDoubleIcon: false,
    },
    {
        id: "html",
        name: "HTML",
        icon: <FileCode className="w-full h-full text-orange-400" />,
        hasDoubleIcon: false,
    },
    {
        id: "javascript",
        name: "JavaScript",
        icon: <Codepen className="w-full h-full text-yellow-400" />,
        hasDoubleIcon: false,
    },
    {
        id: "java",
        name: "Java",
        icon: <Coffee className="w-full h-full text-red-400" />,
        hasDoubleIcon: false,
    },
    {
        id: "cpp",
        name: "C++",
        icon: <Cpu className="w-full h-full text-blue-500" />,
        hasDoubleIcon: false,
    },
    {
        id: "sql",
        name: "SQL",
        icon: <Database className="w-full h-full text-green-400" />,
        hasDoubleIcon: false,
    },
];

const additionalLinks = [
    { id: "link2", icon: <Globe className="w-8 h-8 text-slate-400" />, alt: "Link 2" },
    { id: "link3", icon: <Share2 className="w-8 h-8 text-slate-400" />, alt: "Link 3" },
    { id: "link4", icon: <Settings className="w-8 h-8 text-slate-400" />, alt: "Link 4" },
    { id: "link5", icon: <HelpCircle className="w-8 h-8 text-slate-400" />, alt: "Link 5" },
    { id: "link6", icon: <BarChart3 className="w-8 h-8 text-slate-400" />, alt: "Link 6" },
    { id: "link7", icon: <Layers className="w-8 h-8 text-slate-400" />, alt: "Link 7" },
    { id: "link8", icon: <Zap className="w-8 h-8 text-slate-400" />, alt: "Link 8" },
    { id: "link9", icon: <Shield className="w-8 h-8 text-slate-400" />, alt: "Link 9" },
    { id: "link10", icon: <MessageSquare className="w-8 h-8 text-slate-400" />, alt: "Link 10" },
];

export const Home = () => {
    const navigate = useNavigate();
    const [isHoveringGetStarted, setIsHoveringGetStarted] = useState(false);
    const [isHoveringSignIn, setIsHoveringSignIn] = useState(false);

    return (
        <main className="relative min-h-screen w-full bg-[#1b1529] text-white flex flex-col items-center justify-center overflow-hidden">

            {/* Background elements */}
            <div className="absolute inset-0 pointer-events-none">
                <div className="absolute top-1/4 left-1/4 w-96 h-96 bg-purple-600/10 rounded-full blur-[120px]" />
                <div className="absolute bottom-1/4 right-1/4 w-96 h-96 bg-blue-600/10 rounded-full blur-[120px]" />
            </div>

            <section className="relative z-10 flex flex-col items-center gap-12 max-w-4xl px-6">
                <h1 className="text-4xl md:text-5xl lg:text-6xl font-serif text-center text-white/90 leading-tight">
                    The free, fun, and effective way
                    <br />
                    to learn to code!
                </h1>

                <div className="flex flex-col gap-6 w-full max-w-[350px]">
                    <button
                        onClick={() => navigate('/register')}
                        className="w-full h-14 bg-[#784b99] rounded-2xl font-bold text-lg tracking-widest uppercase shadow-[0px_6px_0px_#4c2b66] hover:translate-y-[2px] hover:shadow-[0px_4px_0px_#4c2b66] active:translate-y-[6px] active:shadow-none transition-all"
                        onMouseEnter={() => setIsHoveringGetStarted(true)}
                        onMouseLeave={() => setIsHoveringGetStarted(false)}
                    >
                        GET STARTED
                    </button>

                    <button
                        onClick={() => navigate('/login')}
                        className="w-full h-14 bg-[#3c3549] rounded-2xl border-2 border-[#b3b3b3] font-bold text-[#29abe2] text-lg tracking-widest uppercase shadow-[0px_5px_0px_#b3b3b3] hover:translate-y-[1px] hover:shadow-[0px_4px_0px_#b3b3b3] active:translate-y-[5px] active:shadow-none transition-all"
                        onMouseEnter={() => setIsHoveringSignIn(true)}
                        onMouseLeave={() => setIsHoveringSignIn(false)}
                    >
                        I ALREADY HAVE AN ACCOUNT
                    </button>
                </div>
            </section>

            {/* Mascot Integration */}
            <div className="relative mt-12 z-0">
                <div className="absolute inset-0 bg-indigo-500/20 blur-3xl rounded-full" />
                <img
                    className="w-80 md:w-[450px] aspect-square object-contain relative drop-shadow-2xl"
                    alt="Trellis mascot"
                    src={trellisMascot}
                />
            </div>

            {/* Footer Navigation */}
            <footer className="fixed bottom-0 left-0 w-full h-24 bg-[#174c70] border-t border-white/5 flex items-center px-10 z-50">
                <div className="max-w-7xl mx-auto w-full flex items-center gap-8 overflow-x-auto scrollbar-hide py-2">

                    <button className="flex-shrink-0 opacity-60 hover:opacity-100 transition-opacity">
                        <ChevronLeft className="w-8 h-8" />
                    </button>

                    <nav className="flex items-center gap-12">
                        {languageItems.map((lang) => (
                            <button
                                key={lang.id}
                                className="flex items-center gap-3 opacity-70 hover:opacity-100 transition-all group"
                            >
                                <div className="w-10 h-10 flex items-center justify-center p-1 bg-white/5 rounded-lg group-hover:bg-white/10 transition-colors">
                                    {lang.icon}
                                </div>
                                <span className="text-xl font-medium tracking-wide whitespace-nowrap">
                                    {lang.name}
                                </span>
                            </button>
                        ))}

                        <div className="h-10 w-[2px] bg-white/10 mx-2" />

                        {additionalLinks.map((linkItem) => (
                            <div
                                key={linkItem.id}
                                className="opacity-50 hover:opacity-100 transition-all cursor-pointer flex-shrink-0 hover:scale-110"
                            >
                                {linkItem.icon}
                            </div>
                        ))}
                    </nav>

                    <button className="flex-shrink-0 opacity-60 hover:opacity-100 transition-opacity ml-auto">
                        <ChevronRight className="w-8 h-8" />
                    </button>
                </div>
            </footer>
        </main>
    );
};

export default Home;
