import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Lottie from 'lottie-react';
import { Mail, Lock, LogIn, ChevronRight } from 'lucide-react';
import loginAnim from '../assets/login-animation.json';

const Login = () => {
    const [formData, setFormData] = useState({ email: '', password: '' });
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.post('http://localhost:5000/api/auth/login', formData);
            if (res.data.token) {
                localStorage.setItem('token', res.data.token);
                navigate('/dashboard', { state: { user: res.data.user } });
            }
        } catch (err) {
            alert(err.response?.data?.msg || "Login failed");
        }
    };

    return (
        <div className="min-h-screen flex items-center justify-center p-6 bg-[#0F172A]">
            <motion.div
                initial={{ scale: 0.95, opacity: 0 }}
                animate={{ scale: 1, opacity: 1 }}
                className="w-full max-w-md"
            >
                <div className="glass-card p-12 relative overflow-hidden">
                    {/* Decorative elements */}
                    <div className="absolute top-0 right-0 w-32 h-32 bg-indigo-500/10 rounded-full -mr-16 -mt-16 blur-3xl" />
                    <div className="absolute bottom-0 left-0 w-32 h-32 bg-blue-500/10 rounded-full -ml-16 -mb-16 blur-3xl" />

                    <div className="flex flex-col items-center mb-10">
                        <div className="h-24 w-24 mb-6">
                            <Lottie animationData={loginAnim} loop={true} />
                        </div>
                        <h2 className="text-4xl font-bold text-white italic tracking-tight">Welcome Back</h2>
                        <p className="text-slate-400 mt-2">Log in to your Trellis account</p>
                    </div>

                    <form onSubmit={handleLogin} className="space-y-6">
                        <div className="relative">
                            <Mail className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-400 w-5 h-5" />
                            <input
                                type="email"
                                placeholder="Email Address"
                                className="glass-input pl-11"
                                value={formData.email}
                                onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                                required
                            />
                        </div>
                        <div className="relative">
                            <Lock className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-400 w-5 h-5" />
                            <input
                                type="password"
                                placeholder="Password"
                                className="glass-input pl-11"
                                value={formData.password}
                                onChange={(e) => setFormData({ ...formData, password: e.target.value })}
                                required
                            />
                        </div>

                        <button type="submit" className="btn-blue flex items-center justify-center gap-2 group shadow-lg shadow-blue-500/20">
                            Sign In <ChevronRight className="w-5 h-5 group-hover:translate-x-1 transition-transform" />
                        </button>
                    </form>

                    <div className="mt-10 text-center">
                        <p className="text-slate-500 text-sm">
                            New here?{' '}
                            <button
                                onClick={() => navigate('/register')}
                                className="text-indigo-400 hover:text-indigo-300 font-medium underline underline-offset-4"
                            >
                                Create an account
                            </button>
                        </p>
                    </div>
                </div>
            </motion.div>
        </div>
    );
};

export default Login;
