import React, { useState } from 'react';
import Lottie from 'lottie-react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

// Assets
import mascot from '../assets/trellis_mascot.png';
import logo from '../assets/Logo.jpeg';
import authAnimation from '../assets/login-animation.json';

const Auth = () => {
    const [isLogin, setIsLogin] = useState(true);
    const [formData, setFormData] = useState({ username: '', email: '', password: '' });
    const navigate = useNavigate();

    const handleAction = async (e) => {
        e.preventDefault();
        try {
            const endpoint = isLogin ? '/login' : '/register';
            const res = await axios.post(`http://localhost:5000/api/auth${endpoint}`, formData);
            if (res.data.user) {
                navigate('/dashboard', { state: { user: res.data.user } });
            }
        } catch (err) {
            alert(err.response?.data?.msg || "Login failed");
        }
    };

    return (
        <div className="min-h-screen w-full flex items-center justify-center p-4">
            <div className="w-full max-w-5xl flex flex-col md:flex-row items-center gap-12 justify-between">

                {/* LEFT SIDE: Brand Identity */}
                <div className="flex flex-col items-center space-y-4 text-center">
                    <img src={mascot} alt="Mascot" className="w-32 h-auto animate-bounce-slow drop-shadow-2xl" />
                    <img src={logo} alt="Trellis" className="h-16 w-auto object-contain" />
                    <p className="text-slate-400 text-lg max-w-xs font-light">
                        Your peer-to-peer programming guardian.
                    </p>
                </div>

                {/* RIGHT SIDE: Auth Card */}
                <div className="glass-card p-10 rounded-[2.5rem] w-full max-w-md flex flex-col">
                    <h2 className="text-4xl font-bold text-white mb-8 text-center tracking-tight">
                        {isLogin ? 'Welcome Back!' : 'Get Started'}
                    </h2>

                    <form onSubmit={handleAction} className="space-y-5">
                        {!isLogin && (
                            <input
                                type="text"
                                placeholder="Full Name"
                                className="w-full bg-slate-800/40 border border-white/10 rounded-xl py-3 px-4 text-white focus:ring-2 focus:ring-blue-500 outline-none transition-all"
                                onChange={(e) => setFormData({ ...formData, username: e.target.value })}
                            />
                        )}
                        <input
                            type="email"
                            placeholder="Email Address"
                            className="w-full bg-slate-800/40 border border-white/10 rounded-xl py-3 px-4 text-white focus:ring-2 focus:ring-blue-500 outline-none transition-all"
                            onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                        />
                        <input
                            type="password"
                            placeholder="Password"
                            className="w-full bg-slate-800/40 border border-white/10 rounded-xl py-3 px-4 text-white focus:ring-2 focus:ring-blue-500 outline-none transition-all"
                            onChange={(e) => setFormData({ ...formData, password: e.target.value })}
                        />

                        {/* Animation Box */}
                        <div className="w-full h-20 flex justify-center items-center overflow-hidden">
                            <Lottie animationData={authAnimation} loop={true} className="h-28 opacity-80" />
                        </div>

                        <button type="submit" className="w-full bg-gradient-to-r from-orange-500 to-rose-500 hover:opacity-90 text-white font-black py-4 rounded-full text-lg transition-all shadow-lg active:scale-95">
                            {isLogin ? 'SIGN IN' : 'CREATE ACCOUNT'}
                        </button>
                    </form>

                    <button
                        onClick={() => setIsLogin(!isLogin)}
                        className="text-slate-400 mt-6 text-sm hover:text-white transition-colors font-medium"
                    >
                        {isLogin ? "Need an account? Sign up" : "Already have an account? Log in"}
                    </button>
                </div>

            </div>
        </div>
    );
};

export default Auth;
