import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
    ChevronLeft,
    User,
    Mail,
    BookOpen,
    Target,
    Save,
    CheckCircle2,
    AlertCircle,
    Loader2,
    MapPin,
    GraduationCap,
    Clock,
    Globe2
} from 'lucide-react';
import Sidebar from '../components/Sidebar';

const Settings = () => {
    const navigate = useNavigate();
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [saving, setSaving] = useState(false);
    const [status, setStatus] = useState({ type: null, message: '' });

    const [formData, setFormData] = useState({
        username: '',
        email: '',
        major: '',
        year: '',
        onboardingGoal: '',
        onboardingLevel: '',
        onboardingCommitment: '',
        onboardingLanguage: ''
    });

    useEffect(() => {
        const fetchUserData = async () => {
            const token = localStorage.getItem('token');
            if (!token) return navigate('/login');

            try {
                const res = await axios.get('http://127.0.0.1:5000/api/auth/me', {
                    headers: { 'x-auth-token': token }
                });
                setUser(res.data);
                setFormData({
                    username: res.data.username || '',
                    email: res.data.email || '',
                    major: res.data.major || '',
                    year: res.data.year || '',
                    onboardingGoal: res.data.onboardingGoal || '',
                    onboardingLevel: res.data.onboardingLevel || '',
                    onboardingCommitment: res.data.onboardingCommitment || '',
                    onboardingLanguage: res.data.onboardingLanguage || ''
                });
                setLoading(false);
            } catch (err) {
                console.error("Error fetching user data:", err);
                setLoading(false);
            }
        };

        fetchUserData();
    }, [navigate]);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setSaving(true);
        setStatus({ type: null, message: '' });

        const token = localStorage.getItem('token');
        try {
            const res = await axios.put('http://127.0.0.1:5000/api/auth/settings', formData, {
                headers: { 'x-auth-token': token }
            });
            setUser(res.data);
            setStatus({ type: 'success', message: 'Profile updated successfully!' });
            setTimeout(() => setStatus({ type: null, message: '' }), 3000);
        } catch (err) {
            console.error("Error updating settings:", err);
            setStatus({ type: 'error', message: err.response?.data?.msg || 'Failed to update profile.' });
        } finally {
            setSaving(false);
        }
    };

    if (loading) {
        return (
            <div className="flex h-screen bg-[#111827] items-center justify-center">
                <Loader2 className="w-10 h-10 text-blue-500 animate-spin" />
            </div>
        );
    }

    return (
        <div className="flex min-h-screen bg-[#111827] text-white font-['Inter',sans-serif]">
            <Sidebar />

            <main className="flex-1 ml-16 md:ml-24 p-8 lg:p-12 overflow-y-auto h-screen invisible-scrollbar">
                <div className="max-w-3xl mx-auto space-y-10">

                    {/* Header */}
                    <div className="flex items-center justify-between">
                        <div className="space-y-2">
                            <button
                                onClick={() => navigate('/dashboard')}
                                className="flex items-center gap-2 text-slate-400 hover:text-white transition-colors font-black uppercase text-xs mb-4 group"
                            >
                                <ChevronLeft className="w-4 h-4 group-hover:-translate-x-1 transition-transform" />
                                <span>Back to Learning</span>
                            </button>
                            <h1 className="text-4xl font-black italic uppercase tracking-tighter text-white">
                                Settings
                            </h1>
                            <div className="h-1.5 w-20 bg-blue-600 rounded-full" />
                        </div>
                    </div>

                    <form onSubmit={handleSubmit} className="space-y-8">

                        {/* Status Messages */}
                        <AnimatePresence>
                            {status.message && (
                                <motion.div
                                    initial={{ opacity: 0, y: -20 }}
                                    animate={{ opacity: 1, y: 0 }}
                                    exit={{ opacity: 0, y: -20 }}
                                    className={`p-4 rounded-2xl border flex items-center gap-3 ${status.type === 'success'
                                            ? 'bg-emerald-500/10 border-emerald-500/30 text-emerald-400'
                                            : 'bg-red-500/10 border-red-500/30 text-red-400'
                                        }`}
                                >
                                    {status.type === 'success' ? <CheckCircle2 className="w-5 h-5" /> : <AlertCircle className="w-5 h-5" />}
                                    <span className="font-bold text-sm tracking-tight">{status.message}</span>
                                </motion.div>
                            )}
                        </AnimatePresence>

                        {/* Profile Section */}
                        <div className="bg-[#1f2937]/50 border border-white/5 rounded-[2.5rem] p-8 space-y-6">
                            <div className="flex items-center gap-3 mb-2">
                                <div className="p-2 bg-blue-500/10 rounded-xl">
                                    <User className="w-5 h-5 text-blue-400" />
                                </div>
                                <h3 className="text-xl font-black italic uppercase tracking-tight">Profile Information</h3>
                            </div>

                            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                <InputGroup
                                    label="Username"
                                    name="username"
                                    value={formData.username}
                                    onChange={handleChange}
                                    icon={<User className="w-4 h-4" />}
                                />
                                <InputGroup
                                    label="Email Address"
                                    name="email"
                                    value={formData.email}
                                    onChange={handleChange}
                                    icon={<Mail className="w-4 h-4" />}
                                    disabled
                                />
                                <InputGroup
                                    label="Major / Field of Study"
                                    name="major"
                                    value={formData.major}
                                    onChange={handleChange}
                                    icon={<GraduationCap className="w-4 h-4" />}
                                    placeholder="e.g. Computer Science"
                                />
                                <InputGroup
                                    label="Year of Study"
                                    name="year"
                                    value={formData.year}
                                    onChange={handleChange}
                                    icon={<Clock className="w-4 h-4" />}
                                    placeholder="e.g. 2nd Year"
                                />
                            </div>
                        </div>

                        {/* Learning Preferences */}
                        <div className="bg-[#1f2937]/50 border border-white/5 rounded-[2.5rem] p-8 space-y-6">
                            <div className="flex items-center gap-3 mb-2">
                                <div className="p-2 bg-orange-500/10 rounded-xl">
                                    <Target className="w-5 h-5 text-orange-400" />
                                </div>
                                <h3 className="text-xl font-black italic uppercase tracking-tight">Learning Preferences</h3>
                            </div>

                            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                <SelectGroup
                                    label="Primary Goal"
                                    name="onboardingGoal"
                                    value={formData.onboardingGoal}
                                    onChange={handleChange}
                                    icon={<Target className="w-4 h-4" />}
                                    options={[
                                        { value: 'career', label: 'Career Switch' },
                                        { value: 'skill', label: 'New Skill' },
                                        { value: 'project', label: 'Build a Project' },
                                        { value: 'other', label: 'Curiosity' }
                                    ]}
                                />
                                <SelectGroup
                                    label="Experience Level"
                                    name="onboardingLevel"
                                    value={formData.onboardingLevel}
                                    onChange={handleChange}
                                    icon={<BookOpen className="w-4 h-4" />}
                                    options={[
                                        { value: 'beginner', label: 'Total Beginner' },
                                        { value: 'intermediate', label: 'I know some basics' },
                                        { value: 'advanced', label: 'Advanced Coder' }
                                    ]}
                                />
                                <SelectGroup
                                    label="Daily Commitment"
                                    name="onboardingCommitment"
                                    value={formData.onboardingCommitment}
                                    onChange={handleChange}
                                    icon={<Clock className="w-4 h-4" />}
                                    options={[
                                        { value: '15', label: '15 mins / day' },
                                        { value: '30', label: '30 mins / day' },
                                        { value: '60', label: '1 hour / day' },
                                        { value: '120', label: '2+ hours / day' }
                                    ]}
                                />
                                <SelectGroup
                                    label="Primary Language"
                                    name="onboardingLanguage"
                                    value={formData.onboardingLanguage}
                                    onChange={handleChange}
                                    icon={<Globe2 className="w-4 h-4" />}
                                    options={[
                                        { value: 'javascript', label: 'JavaScript' },
                                        { value: 'python', label: 'Python' },
                                        { value: 'html', label: 'HTML / CSS' },
                                        { value: 'java', label: 'Java' },
                                        { value: 'c', label: 'C / C++' }
                                    ]}
                                />
                            </div>
                        </div>

                        {/* Action Buttons */}
                        <div className="flex items-center justify-end gap-4 pb-10">
                            <button
                                type="button"
                                onClick={() => navigate('/dashboard')}
                                className="px-8 py-4 text-slate-400 hover:text-white font-black italic uppercase text-xs transition-colors"
                            >
                                Cancel
                            </button>
                            <button
                                type="submit"
                                disabled={saving}
                                className="px-10 py-4 bg-blue-600 hover:bg-blue-500 disabled:bg-blue-600/50 text-white font-black italic uppercase text-sm rounded-2xl transition-all active:scale-95 shadow-xl shadow-blue-900/20 flex items-center gap-2"
                            >
                                {saving ? (
                                    <Loader2 className="w-4 h-4 animate-spin" />
                                ) : (
                                    <Save className="w-4 h-4" />
                                )}
                                <span>{saving ? 'Saving...' : 'Save Changes'}</span>
                            </button>
                        </div>

                    </form>
                </div>
            </main>
        </div>
    );
};

const InputGroup = ({ label, icon, ...props }) => (
    <div className="space-y-2">
        <label className="text-[10px] font-black text-slate-500 uppercase tracking-widest ml-1">{label}</label>
        <div className="relative group">
            <div className={`absolute left-4 top-1/2 -translate-y-1/2 transition-colors ${props.disabled ? 'text-slate-600' : 'text-slate-500 group-focus-within:text-blue-400'}`}>
                {icon}
            </div>
            <input
                {...props}
                className={`w-full bg-[#111827] border border-white/5 rounded-2xl py-4 pl-12 pr-4 text-sm font-bold tracking-tight transition-all outline-none focus:border-blue-500/50 focus:bg-blue-500/5 ${props.disabled ? 'opacity-50 cursor-not-allowed text-slate-400' : 'text-white'
                    }`}
            />
        </div>
    </div>
);

const SelectGroup = ({ label, icon, options, ...props }) => (
    <div className="space-y-2">
        <label className="text-[10px] font-black text-slate-500 uppercase tracking-widest ml-1">{label}</label>
        <div className="relative group">
            <div className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-500 group-focus-within:text-blue-400 transition-colors pointer-events-none">
                {icon}
            </div>
            <select
                {...props}
                className="w-full bg-[#111827] border border-white/5 rounded-2xl py-4 pl-12 pr-4 text-sm font-bold tracking-tight transition-all outline-none focus:border-blue-500/50 focus:bg-blue-500/5 text-white appearance-none cursor-pointer"
            >
                <option value="">Select an option</option>
                {options.map(opt => (
                    <option key={opt.value} value={opt.value}>{opt.label}</option>
                ))}
            </select>
            <div className="absolute right-4 top-1/2 -translate-y-1/2 pointer-events-none text-slate-500">
                <ChevronLeft className="w-4 h-4 -rotate-90" />
            </div>
        </div>
    </div>
);

export default Settings;
