import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import Lottie from 'lottie-react';
import { Eye, EyeOff } from 'lucide-react';
import animationData from './assets/login-animation.json';
import logo from './assets/Logo.jpeg';
import './Login.css';

const Login = () => {
    const navigate = useNavigate();
    const [showPassword, setShowPassword] = useState(false);
    const lottieRef = useRef(null);

    const handleInputFocus = (inputType) => {
        if (lottieRef.current) {
            if (inputType === 'password') {
                lottieRef.current.playSegments([30, 60], true);
            } else {
                lottieRef.current.playSegments([0, 30], true);
            }
        }
    };

    return (
        <div className="space-background">
            <div className="stars"></div>
            <div className="login-card">
                <img src={logo} alt="Trellis" className="brand-logo" />
                <div className="animation-container">
                    <Lottie lottieRef={lottieRef} animationData={animationData} loop={true} />
                </div>
                <form className="login-form">
                    <h2 className="page-title">Login</h2>
                    <div className="input-group">
                        <input type="email" placeholder="Email Address" onFocus={() => handleInputFocus('email')} required />
                    </div>
                    <div className="input-group">
                        <input type={showPassword ? "text" : "password"} placeholder="Password" onFocus={() => handleInputFocus('password')} required />
                        <button type="button" className="eye-icon-btn" onClick={() => setShowPassword(!showPassword)}>
                            {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
                        </button>
                    </div>
                    <button type="submit" className="primary-btn">LOGIN</button>
                    <div className="footer-text">
                        <span>New to Trellis?</span>
                        <button type="button" className="link-btn" onClick={() => navigate('/register')}>Sign Up</button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default Login;
