import React from 'react';
import { useLocation } from 'react-router-dom';
import mascot from '../assets/trellis_mascot.png';

const Dashboard = () => {
    const { state } = useLocation();
    const user = state?.user || { username: "Scholar", progress: 10 };

    return (
        <div className="min-h-screen bg-[#0F172A] p-10 flex flex-col items-center space-y-10 text-white">
            {/* Welcome Card */}
            <div className="glass-panel p-10 w-full max-w-4xl flex flex-col md:flex-row items-center gap-10">
                <img src={mascot} alt="Mascot" className="w-28" />
                <div className="flex-grow space-y-4 text-center md:text-left">
                    <h2 className="text-5xl font-black italic tracking-tighter">
                        Welcome back, {user.username}!
                    </h2>
                    <div className="w-full bg-slate-800 rounded-full h-3 border border-white/5">
                        <div
                            className="bg-blue-500 h-full rounded-full shadow-[0_0_15px_rgba(59,130,246,0.5)] transition-all duration-1000"
                            style={{ width: `${user.progress}%` }}
                        ></div>
                    </div>
                    <p className="text-blue-400 font-black text-sm uppercase tracking-widest">
                        Course Completion: {user.progress}%
                    </p>
                </div>
            </div>

            {/* Action Grid */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-8 w-full max-w-4xl">
                <button className="glass-panel p-10 font-black text-2xl hover:bg-white/10 transition-all">
                    MATCHMAKING AI
                </button>
                <button className="glass-panel p-10 font-black text-2xl hover:bg-white/10 transition-all">
                    KTU RESOURCES
                </button>
            </div>
        </div>
    );
};

export default Dashboard;
