import React from 'react';
import { motion } from 'framer-motion';
import {
    Layout,
    Shield,
    Trophy,
    Target,
    BookOpen,
    Settings,
    ChevronRight,
    ChevronLeft,
    Lock,
    Eye,
    Star,
    Flame,
    Crown,
    Map,
    Play
} from 'lucide-react';
import YipHead from '../assets/Yip_head.png';
import mascot from '../assets/trellis_mascot.png';
import { useNavigate } from 'react-router-dom';
import { courseData } from '../data/courses';
import axios from 'axios';

const Dashboard = () => {
    const navigate = useNavigate();
    const [user, setUser] = React.useState(null);
    const [currentCourseId, setCurrentCourseId] = React.useState(localStorage.getItem('selectedCourse') || 'html');
    const [currentSectionId, setCurrentSectionId] = React.useState(parseInt(localStorage.getItem('selectedSectionId')) || 1);

    React.useEffect(() => {
        const fetchUserData = async () => {
            const token = localStorage.getItem('token');
            if (!token) return;

            try {
                const res = await axios.get('http://localhost:5000/api/auth/me', {
                    headers: { 'x-auth-token': token }
                });
                setUser(res.data);
                if (res.data.currentCourse) {
                    setCurrentCourseId(res.data.currentCourse);
                    localStorage.setItem('selectedCourse', res.data.currentCourse);
                }
                if (res.data.currentSectionId) {
                    setCurrentSectionId(res.data.currentSectionId);
                    localStorage.setItem('selectedSectionId', res.data.currentSectionId);
                }
            } catch (err) {
                console.error("Error fetching user data:", err);
            }
        };

        fetchUserData();
    }, []);

    const currentCourse = courseData[currentCourseId] || courseData.html;
    const currentSection = currentCourse.sections.find(s => s.id === currentSectionId) || currentCourse.sections[0];

    const dailyGoals = [
        { id: 1, title: 'Earn 90 XP', current: user?.xp || 0, total: 90, icon: <Shield className="w-5 h-5 text-blue-400" /> },
        { id: 2, title: 'Complete 3 exercises', current: user?.exercisesCompletedToday || 0, total: 3, icon: <BookOpen className="w-5 h-5 text-blue-400" /> },
        { id: 3, title: 'Solve 2 challenges on the first try', current: user?.firstTrySolves || 0, total: 2, icon: <Target className="w-5 h-5 text-red-400" /> }
    ];

    const handleLessonClick = (nodeId) => {
        navigate(`/lesson/${currentCourseId}/${currentSectionId}/${nodeId}`);
    };

    const learningPath = currentSection.lessons;

    return (
        <div className="flex min-h-screen bg-[#111827] text-white font-['Inter',sans-serif]">

            {/* LEFT SIDEBAR: Navigation */}
            <aside className="w-16 md:w-24 fixed left-0 top-0 bottom-0 bg-[#1f2937] border-r border-white/5 flex flex-col items-center py-6 z-50">
                <div className="mb-8 px-2 transition-transform hover:scale-110 cursor-pointer" onClick={() => navigate('/dashboard')}>
                    <img src={YipHead} alt="Yip" className="w-full h-auto drop-shadow-[0_4px_15px_rgba(74,144,226,0.5)]" />
                </div>

                <nav className="flex flex-col gap-6">
                    <NavItem icon={<Map className="w-6 h-6" />} active />
                    <NavItem icon={<Trophy className="w-6 h-6" />} onClick={() => navigate('/leaderboard')} />
                    <NavItem icon={<BookOpen className="w-6 h-6" />} onClick={() => navigate('/sections')} />
                    <NavItem icon={<Target className="w-6 h-6" />} onClick={() => navigate('/streak')} />
                </nav>

                <div className="mt-auto">
                    <NavItem icon={<Settings className="w-7 h-7" />} />
                </div>
            </aside>

            {/* MAIN CONTENT AREA */}
            <main className="flex-1 ml-16 md:ml-24 mr-0 lg:mr-[300px] p-4 lg:p-6 flex flex-col items-center">

                {/* Learning Path Header */}
                <div className="w-full max-w-2xl bg-[#1e293b] rounded-2xl p-6 mb-12 relative overflow-hidden shadow-2xl border border-white/5">
                    <div className="relative z-10">
                        <button
                            onClick={() => navigate('/sections')}
                            className="flex items-center gap-1.5 text-blue-400 text-[10px] font-black uppercase tracking-widest mb-2 hover:text-blue-300 transition-colors group"
                        >
                            <ChevronLeft className="w-3 h-3 group-hover:-translate-x-0.5 transition-transform" />
                            <span>Section {currentSection.id}: {currentSection.title}</span>
                        </button>
                        <h1 className="text-3xl font-black italic tracking-tighter uppercase">{currentCourse.title}</h1>
                    </div>
                    <div className="absolute top-0 right-0 w-32 h-32 bg-blue-500/10 blur-[60px] rounded-full -mr-16 -mt-16" />
                </div>

                <div className="flex flex-col items-center gap-0 relative pb-32">
                    {/* Path Label - High Fidelity */}
                    <div className="mb-12 flex items-center gap-4 w-screen max-w-lg justify-center">
                        <div className="h-px flex-1 bg-gradient-to-r from-transparent to-slate-800" />
                        <span className="text-slate-500 text-[10px] font-black uppercase tracking-[0.3em] italic">{currentCourse.title}</span>
                        <div className="h-px flex-1 bg-gradient-to-l from-transparent to-slate-800" />
                    </div>

                    {learningPath.map((node, index) => {
                        const courseJourney = user?.enrolledJourneys?.find(j => j.courseId === currentCourseId);
                        const courseProgress = courseJourney?.progress || 0;

                        let status = 'locked';
                        if (index < courseProgress) status = 'completed';
                        else if (index === courseProgress) status = 'current';

                        return (
                            <HexPathNode
                                key={node.id}
                                node={{ ...node, status }}
                                index={index}
                                isLast={index === learningPath.length - 1}
                                onClick={() => handleLessonClick(node.id)}
                            />
                        );
                    })}
                </div>
            </main>

            {/* RIGHT SIDEBAR: Stats & Goals */}
            <aside className="hidden lg:flex w-[300px] fixed right-0 top-0 bottom-0 bg-[#111827] border-l border-white/5 flex-col p-6 gap-6 invisible-scrollbar overflow-y-auto">

                {/* Stats Bar */}
                <div className="flex justify-between items-center bg-[#1f2937] p-3 rounded-xl border border-white/5 shadow-lg relative">
                    <StatItem value={user?.xp || 0} type="xp">
                        <JourneysPopover enrolledJourneys={user?.enrolledJourneys || []} currentCourseId={currentCourseId} />
                    </StatItem>
                    <StatItem value={user?.streak || 0} type="streak">
                        <StreakPopover streak={user?.streak || 0} />
                    </StatItem>
                </div>

                {/* Leaderboard Section */}
                <div className="bg-[#1f2937] rounded-2xl p-5 border border-white/5 shadow-xl">
                    <div className="flex justify-between items-center mb-4">
                        <h3 className="text-lg font-bold italic uppercase tracking-tighter">Leaderboard</h3>
                        <button className="text-blue-400 text-xs font-bold hover:underline">View</button>
                    </div>
                    <div className="flex flex-col items-center py-8 bg-[#2d3748]/50 rounded-xl border border-white/5 relative overflow-hidden">
                        <Trophy className="w-10 h-10 text-yellow-500/20 mb-2" />
                        <p className="text-slate-500 text-center text-[10px] font-black uppercase tracking-widest px-4">
                            Coming Soon
                        </p>
                        <div className="absolute inset-0 bg-gradient-to-t from-blue-500/5 to-transparent pointer-events-none" />
                    </div>
                </div>

                {/* Daily Goals Section */}
                <div className="bg-[#1f2937] rounded-2xl p-5 border border-white/5 shadow-xl">
                    <div className="flex justify-between items-center mb-4">
                        <h3 className="text-lg font-bold italic uppercase tracking-tighter">Daily Goals</h3>
                        <button className="text-blue-400 text-xs font-bold hover:underline">View</button>
                    </div>

                    <div className="space-y-4">
                        {dailyGoals.map(goal => (
                            <div key={goal.id} className="space-y-1.5">
                                <div className="flex justify-between items-center">
                                    <span className="text-xs font-bold text-slate-300">{goal.title}</span>
                                    <span className="text-[10px] font-black text-slate-500">{goal.current}/{goal.total}</span>
                                </div>
                                <div className="h-2 w-full bg-slate-800 rounded-full overflow-hidden relative">
                                    <div
                                        className={`h-full bg-gradient-to-r from-blue-500 to-indigo-500 rounded-full`}
                                        style={{ width: `${(goal.current / goal.total) * 100}%` }}
                                    />
                                </div>
                            </div>
                        ))}
                    </div>
                </div>


            </aside>
        </div>
    );
};

const NavItem = ({ icon, active = false, onClick }) => (
    <button
        onClick={onClick}
        className={`w-10 h-10 flex items-center justify-center rounded-xl transition-all ${active ? 'bg-blue-600 text-white shadow-lg shadow-blue-600/30' : 'text-slate-400 hover:bg-white/5 hover:text-white'
            }`}>
        {icon}
    </button>
);

const StatItem = ({ value, type, children }) => (
    <div className="relative group/stat">
        <div className="flex flex-col items-center gap-0.5 p-2 bg-slate-800/40 rounded-xl border border-white/5 flex-1 min-w-[80px] cursor-pointer hover:bg-slate-800/60 transition-all">
            <div className="w-8 h-8 flex items-center justify-center">
                {type === "xp" ? (
                    <Shield className="w-6 h-6 text-blue-400" />
                ) : (
                    <Flame className="w-6 h-6 text-orange-500" />
                )}
            </div>
            <div className="flex items-center gap-1">
                <span className="text-[10px] font-black text-slate-300">{value}</span>
                <span className="text-[8px] font-bold text-slate-500 uppercase tracking-tighter">{type}</span>
            </div>
        </div>

        {/* Popover Container */}
        <div className="absolute top-0 right-[110%] invisible group-hover/stat:visible transition-all duration-300 z-[100]">
            {children}
        </div>
    </div>
);

const JourneysPopover = ({ enrolledJourneys = [], currentCourseId }) => {
    const navigate = useNavigate();

    const handleCourseSwitch = async (courseId, sectionId) => {
        const token = localStorage.getItem('token');
        if (token) {
            try {
                await axios.patch('http://localhost:5000/api/auth/progress',
                    { currentCourse: courseId, currentSectionId: sectionId || 1 },
                    { headers: { 'x-auth-token': token } }
                );
                localStorage.setItem('selectedCourse', courseId);
                localStorage.setItem('selectedSectionId', sectionId || 1);
                window.location.reload(); // Quick way to refresh dashboard state
            } catch (err) {
                console.error("Error switching course:", err);
            }
        }
    };

    return (
        <div className="w-[300px] bg-[#1f2937] rounded-3xl overflow-hidden shadow-2xl border border-white/10 p-2 transform origin-top transition-transform scale-95 group-hover:scale-100">
            <div className="p-4 border-b border-white/5 flex justify-between items-center">
                <h4 className="text-slate-400 font-bold text-xs uppercase tracking-widest opacity-70">My Journeys</h4>
                <span className="text-[10px] font-black text-blue-500 bg-blue-500/10 px-2 py-0.5 rounded-md uppercase tracking-tighter">
                    {enrolledJourneys.length} Enrolled
                </span>
            </div>

            <div className="p-2 space-y-1 max-h-[320px] overflow-y-auto invisible-scrollbar">
                {enrolledJourneys.length > 0 ? enrolledJourneys.map((journey) => {
                    const courseInfo = courseData[journey.courseId] || { title: journey.courseId };
                    const isActive = journey.courseId === currentCourseId;

                    // Calculate actual progress percentage
                    const totalLessons = courseInfo.sections?.reduce((acc, s) => acc + s.lessons.length, 0) || 10;
                    const progressPercent = Math.min(100, (journey.progress / totalLessons) * 100);

                    return (
                        <button
                            key={journey.courseId}
                            onClick={() => !isActive && handleCourseSwitch(journey.courseId, journey.currentSectionId)}
                            className={`w-full flex items-center gap-4 p-3 rounded-2xl border transition-all text-left group/item relative
                                ${isActive
                                    ? 'bg-blue-600/10 border-blue-500/30'
                                    : 'bg-white/5 border-white/5 hover:bg-white/10 hover:border-white/10'
                                }`}
                        >
                            <div className={`w-12 h-12 rounded-xl flex items-center justify-center font-black text-xl italic shadow-lg text-white shrink-0
                                ${journey.courseId === 'python' ? 'bg-blue-600' :
                                    journey.courseId === 'html' ? 'bg-orange-600' :
                                        journey.courseId === 'javascript' ? 'bg-yellow-600' : 'bg-slate-700'}
                            `}>
                                {journey.courseId.charAt(0).toUpperCase()}
                            </div>

                            <div className="flex-1 min-w-0">
                                <div className="flex justify-between items-center mb-1">
                                    <span className="font-black italic uppercase text-lg tracking-tight text-white truncate">
                                        {courseInfo.title || journey.courseId}
                                    </span>
                                    {isActive && (
                                        <div className="w-2 h-2 rounded-full bg-blue-500 shadow-[0_0_10px_rgba(59,130,246,1)]" />
                                    )}
                                </div>
                                <div className="h-1.5 w-full bg-slate-800 rounded-full overflow-hidden">
                                    <div
                                        className="h-full bg-blue-500 rounded-full"
                                        style={{ width: `${progressPercent}%` }}
                                    />
                                </div>
                            </div>
                        </button>
                    );
                }) : (
                    <div className="py-8 text-center px-4">
                        <p className="text-slate-500 text-xs font-bold uppercase tracking-widest leading-relaxed">
                            No journeys yet.<br />Start your first one!
                        </p>
                    </div>
                )}

                <button
                    onClick={() => navigate('/journeys')}
                    className="w-full flex items-center gap-4 p-4 hover:bg-white/5 rounded-2xl transition-all text-left group/add text-slate-500 hover:text-white mt-1 border border-dashed border-slate-700/50 hover:border-blue-500/50"
                >
                    <div className="w-10 h-10 border-2 border-dashed border-slate-700 rounded-xl flex items-center justify-center group-hover/add:border-blue-500/50 group-hover/add:bg-white/5">
                        <Map className="w-5 h-5" />
                    </div>
                    <span className="font-bold text-base tracking-tight italic uppercase opacity-60 group-hover/add:opacity-100">Add Journey</span>
                </button>
            </div>
        </div>
    );
};

const StreakPopover = ({ streak }) => {
    const navigate = useNavigate();
    return (
        <div className="w-[320px] bg-[#1f2937] rounded-[2.5rem] overflow-hidden shadow-2xl border border-white/10 transform origin-top transition-transform scale-95 group-hover:scale-100">
            {/* Header Section with Reference Gradient */}
            <div className="bg-[#854d0e] p-7 text-left relative overflow-hidden">
                <h4 className="text-3xl font-black italic uppercase tracking-tighter mb-1 relative z-10 text-white">{streak} day streak</h4>
                <p className="text-orange-100/60 text-[12px] font-bold leading-tight max-w-[180px] relative z-10">
                    {streak > 0 ? "You're on fire! Keep it up!" : "Do a lesson today to start a new streak!"}
                </p>
                {/* Mascot flame silhouette */}
                <Flame className="absolute -right-4 -bottom-4 w-36 h-36 text-orange-900/30 rotate-12" />
                <div className="absolute right-8 top-1/2 -translate-y-1/2 w-20 h-20 bg-white/10 rounded-full blur-2xl" />
                <Flame className="absolute right-10 top-1/2 -translate-y-1/2 w-16 h-16 text-white opacity-40 drop-shadow-lg" />
            </div>

            {/* Content Section */}
            <div className="p-7 space-y-8 bg-[#1f2937]">
                {/* Week Progress */}
                <div className="flex justify-between items-center px-2">
                    {['S', 'M', 'T', 'W', 'T', 'F', 'S'].map((day, i) => (
                        <div key={i} className="flex flex-col items-center gap-3">
                            <span className="text-[11px] font-black text-slate-500 tracking-wider font-mono">{day}</span>
                            <div className={`w-9 h-9 rounded-full border-2 flex items-center justify-center transition-all ${i === 6 ? 'border-slate-700 bg-slate-800/30' : 'border-slate-800 bg-slate-800/50 opacity-40'}`}>
                                {i === 6 && <Star className="w-4 h-4 text-slate-700" />}
                            </div>
                        </div>
                    ))}
                </div>

                {/* Streak Club Box - High Fidelity */}
                <div className="bg-[#111827] rounded-[2rem] p-5 border border-white/5 flex gap-5 items-center shadow-inner">
                    <div className="w-14 h-14 rounded-2xl bg-[#1f2937] flex items-center justify-center shrink-0 shadow-lg border border-white/5">
                        <Lock className="w-7 h-7 text-slate-600" />
                    </div>
                    <div className="space-y-3 flex-1">
                        <div>
                            <h5 className="text-sm font-black text-white mb-0.5 uppercase tracking-tight italic">Streak Club</h5>
                            <p className="text-[10px] text-slate-500 font-bold leading-tight uppercase tracking-tighter opacity-60">
                                Reach 7 days to earn perks
                            </p>
                        </div>
                        {/* Progress Bar */}
                        <div className="h-2.5 w-full bg-[#1f2937] rounded-full relative overflow-hidden border border-white/5">
                            <div className="absolute right-0 top-0 bottom-0 w-8 bg-gradient-to-l from-orange-500/20 to-transparent" />
                            <Flame className="absolute right-2 top-1/2 -translate-y-1/2 w-2.5 h-2.5 text-slate-700" />
                        </div>
                    </div>
                </div>

                {/* View More Button */}
                <button
                    onClick={() => navigate('/streak')}
                    className="w-full py-4 bg-[#0891b2] hover:bg-[#0e7490] text-white font-black italic uppercase text-base rounded-2xl transition-all shadow-xl shadow-cyan-900/30 active:scale-95"
                >
                    VIEW MORE
                </button>
            </div>
        </div>
    );
};

const PathNode = ({ node, index, onClick }) => {
    const [isHovered, setIsHovered] = React.useState(false);

    return (
        <motion.div
            initial={{ scale: 0.8, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            transition={{ delay: index * 0.1 }}
            className="relative z-10"
            onMouseEnter={() => setIsHovered(true)}
            onMouseLeave={() => setIsHovered(false)}
            onClick={node.status !== 'locked' ? onClick : undefined}
        >
            <div className={`w-20 h-20 md:w-24 md:h-24 rounded-[1.5rem] flex items-center justify-center border-[6px] transition-all transform hover:scale-110 active:scale-95 cursor-pointer ${node.status === 'current'
                ? 'bg-[#1e4e6e] border-blue-400 shadow-[0_0_30px_rgba(96,165,250,0.4)]'
                : node.status === 'completed'
                    ? 'bg-emerald-500/10 border-emerald-500/50'
                    : 'bg-[#1f2937] border-slate-800'
                }`}>

                {node.status === 'completed' ? (
                    <div className="w-10 h-10 bg-emerald-500 rounded-full flex items-center justify-center shadow-lg">
                        <ChevronRight className="w-6 h-6 text-white" />
                    </div>
                ) : node.status === 'current' ? (
                    <div className="w-12 h-12 md:w-14 md:h-14 bg-blue-500 rounded-full flex items-center justify-center shadow-inner group">
                        <Play className="w-6 h-6 md:w-7 h-7 text-white fill-current animate-pulse" />
                    </div>
                ) : (
                    <Lock className="w-6 h-6 md:w-7 h-7 text-slate-700" />
                )}
            </div>

            {/* Popover for Current Lesson */}
            {node.status === 'current' && (
                <motion.div
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="absolute bottom-[calc(100%+15px)] left-1/2 -translate-x-1/2 bg-[#1f2937] border border-white/10 rounded-2xl p-4 shadow-2xl min-w-[200px] z-50 text-center"
                >
                    <h4 className="text-white font-black italic uppercase text-xs mb-3 truncate px-2">{node.title}</h4>
                    <button
                        onClick={(e) => { e.stopPropagation(); onClick(); }}
                        className="w-full bg-blue-600 hover:bg-blue-500 text-white font-black italic uppercase text-[10px] py-2.5 rounded-xl shadow-lg shadow-blue-900/40 transition-all active:scale-95"
                    >
                        Continue
                    </button>
                    <div className="absolute -bottom-1.5 left-1/2 -translate-x-1/2 w-3 h-3 bg-[#1f2937] border-r border-b border-white/10 rotate-45" />
                </motion.div>
            )}

            {/* Hover Tooltip for non-current/non-locked nodes */}
            {isHovered && node.status !== 'current' && (
                <motion.div
                    initial={{ opacity: 0, x: 10 }}
                    animate={{ opacity: 1, x: 0 }}
                    className="absolute left-[calc(100%+20px)] top-1/2 -translate-y-1/2 bg-[#1f2937] border border-white/5 px-4 py-3 rounded-xl shadow-2xl z-50 text-left min-w-[150px]"
                >
                    <h4 className="text-white font-black italic uppercase text-[10px] mb-1">{node.title}</h4>
                    <p className="text-slate-500 text-[8px] font-bold uppercase tracking-widest leading-tight">
                        {node.status === 'completed' ? 'Lesson Completed!' : 'Locked Challenge'}
                    </p>
                    <div className="absolute top-1/2 -left-1.5 -translate-y-1/2 w-3 h-3 bg-[#1f2937] border-l border-b border-white/5 rotate-45" />
                </motion.div>
            )}
        </motion.div>
    );
};

const JourneysSection = ({ enrolledJourneys = [], currentCourseId }) => {
    const navigate = useNavigate();

    const handleCourseSwitch = async (courseId, sectionId) => {
        const token = localStorage.getItem('token');
        if (token) {
            try {
                await axios.patch('http://localhost:5000/api/auth/progress',
                    { currentCourse: courseId, currentSectionId: sectionId || 1 },
                    { headers: { 'x-auth-token': token } }
                );
                localStorage.setItem('selectedCourse', courseId);
                localStorage.setItem('selectedSectionId', sectionId || 1);
                window.location.reload();
            } catch (err) {
                console.error("Error switching course:", err);
            }
        }
    };

    return (
        <div className="bg-[#1f2937] rounded-2xl p-5 border border-white/5 shadow-xl">
            <div className="flex justify-between items-center mb-4">
                <h3 className="text-lg font-bold italic uppercase tracking-tighter">My Journeys</h3>
                <span className="text-[10px] font-black text-blue-500 bg-blue-500/10 px-2 py-0.5 rounded-md uppercase">
                    {enrolledJourneys.length}
                </span>
            </div>

            <div className="space-y-2 max-h-[200px] overflow-y-auto invisible-scrollbar pr-1">
                {enrolledJourneys.length > 0 ? enrolledJourneys.map((journey) => {
                    const courseInfo = courseData[journey.courseId] || { title: journey.courseId };
                    const isActive = journey.courseId === currentCourseId;
                    const totalLessons = courseInfo.sections?.reduce((acc, s) => acc + s.lessons.length, 0) || 10;
                    const progressPercent = Math.min(100, (journey.progress / totalLessons) * 100);

                    return (
                        <button
                            key={journey.courseId}
                            onClick={() => !isActive && handleCourseSwitch(journey.courseId, journey.currentSectionId)}
                            className={`w-full flex items-center gap-3 p-2 rounded-xl border transition-all text-left relative
                                ${isActive
                                    ? 'bg-blue-600/10 border-blue-500/30'
                                    : 'bg-white/5 border-white/5 hover:bg-white/10 hover:border-white/10'
                                }`}
                        >
                            <div className={`w-8 h-8 rounded-lg flex items-center justify-center font-black text-sm italic shadow-lg text-white shrink-0
                                ${journey.courseId === 'python' ? 'bg-blue-600' :
                                    journey.courseId === 'html' ? 'bg-orange-600' :
                                        journey.courseId === 'javascript' ? 'bg-yellow-600' : 'bg-slate-700'}
                            `}>
                                {journey.courseId.charAt(0).toUpperCase()}
                            </div>
                            <div className="flex-1 min-w-0">
                                <div className="flex justify-between items-center mb-0.5">
                                    <span className="font-bold italic uppercase text-xs tracking-tight text-white truncate px-1">
                                        {courseInfo.title || journey.courseId}
                                    </span>
                                    {isActive && <div className="w-1.5 h-1.5 rounded-full bg-blue-500 shadow-[0_0_5px_rgba(59,130,246,1)]" />}
                                </div>
                                <div className="h-1 w-full bg-slate-800 rounded-full overflow-hidden">
                                    <div className="h-full bg-blue-500 rounded-full" style={{ width: `${progressPercent}%` }} />
                                </div>
                            </div>
                        </button>
                    );
                }) : null}

                <button
                    onClick={() => navigate('/journeys')}
                    className="w-full flex items-center gap-3 p-2 hover:bg-white/5 rounded-xl transition-all text-left group/add text-slate-500 hover:text-white border border-dashed border-slate-700 hover:border-blue-500/50"
                >
                    <div className="w-8 h-8 border border-dashed border-slate-700 rounded-lg flex items-center justify-center group-hover/add:border-blue-500/50">
                        <Map className="w-4 h-4" />
                    </div>
                    <span className="font-bold text-xs tracking-tight italic uppercase opacity-60 group-hover/add:opacity-100">Add</span>
                </button>
            </div>
        </div>
    );
};

export default Dashboard;
