import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import Lottie from 'lottie-react';
import { Eye, EyeOff } from 'lucide-react';
import animationData from './assets/login-animation.json';
import logo from './assets/Logo.jpeg';

const Login = () => {
    const navigate = useNavigate();
    const [showPassword, setShowPassword] = useState(false);
    const lottieRef = useRef(null);

    // Global Reset Injector (replaces index.css)
    const injectGlobal = (
        <style>{`
            @import url('https://fonts.googleapis.com/css2?family=Poppins:wght@300;400;600;800&display=swap');
            * { margin: 0; padding: 0; box-sizing: border-box; font-family: 'Poppins', sans-serif; }
            body, html { background: #000; overflow: hidden; height: 100%; width: 100%; }
        `}</style>
    );

    const styles = {
        container: {
            height: '100vh', width: '100vw',
            background: 'radial-gradient(circle at center, #1b0033 0%, #000000 100%)',
            display: 'flex', justifyContent: 'center', alignItems: 'center', position: 'relative'
        },
        card: {
            background: 'rgba(255, 255, 255, 0.04)',
            backdropFilter: 'blur(25px)',
            WebkitBackdropFilter: 'blur(25px)',
            padding: '50px 45px',
            borderRadius: '40px',
            width: '440px',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            border: '1px solid rgba(255, 255, 255, 0.1)',
            boxShadow: '0 25px 50px rgba(0,0,0,0.6)',
        },
        input: {
            width: '100%', padding: '16px 20px', margin: '10px 0',
            borderRadius: '14px', border: '1px solid rgba(255,255,255,0.1)',
            background: 'rgba(255,255,255,0.05)', color: 'white', fontSize: '15px', outline: 'none'
        },
        btn: {
            width: '100%', padding: '18px', marginTop: '20px',
            background: 'linear-gradient(135deg, #9133ff 0%, #6a00f4 100%)',
            color: 'white', border: 'none', borderRadius: '14px',
            fontWeight: '700', fontSize: '16px', cursor: 'pointer', letterSpacing: '1px',
            transition: '0.3s'
        }
    };

    return (
        <div style={styles.container}>
            {injectGlobal}
            <div style={styles.card}>
                <img src={logo} alt="Trellis" style={{width: '140px', mixBlendMode: 'screen', marginBottom: '5px'}} />
                <div style={{width: '320px', height: '220px'}}>
                    <Lottie lottieRef={lottieRef} animationData={animationData} loop={true} />
                </div>
                <h2 style={{color: 'white', fontSize: '24px', fontWeight: '800', margin: '20px 0', letterSpacing: '3px'}}>LOGIN</h2>
                <div style={{width: '100%'}}>
                    <input style={styles.input} type="email" placeholder="Email Address" required />
                    <div style={{position: 'relative', width: '100%'}}>
                        <input style={styles.input} type={showPassword ? "text" : "password"} placeholder="Password" required />
                        <button type="button" onClick={() => setShowPassword(!showPassword)} style={{position: 'absolute', right: '15px', top: '25px', background: 'none', border: 'none', color: '#777', cursor: 'pointer'}}>
                            {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
                        </button>
                    </div>
                </div>
                <button style={styles.btn}>LOGIN</button>
                <div style={{marginTop: '30px', color: '#999', fontSize: '14px'}}>
                    New to Trellis? <span onClick={() => navigate('/register')} style={{color: '#9133ff', fontWeight: '700', cursor: 'pointer', marginLeft: '5px'}}>Sign Up</span>
                </div>
            </div>
        </div>
    );
};

export default Login;
